import React, { Component } from 'react'
import { Button } from '@material-ui/core'
import { MdPhoneForwarded } from 'react-icons/md'
import Modal from '../../material/modal'
import ContactItem from '../../configuration/contactItem'

const Axios = require('axios')

let style = {
    alignItems: 'center',
    textAlign: 'center',
    justifyContent: 'center',
    width: '100%'
}

class emergencySection extends Component{

    state = {
        openContacts: false,
        contacts: []
    }

    getContacts = async() => {
        let result = await Axios.get('/api/contacts/getContacts')
        console.log(result)
        this.setState({contacts: result.data})
    }

    toggleModal = () => {
        let currentOpen = this.state.openContacts
        if(!currentOpen){
            this.getContacts()
        }
        this.setState({openContacts: !currentOpen})
    }

    makeCall = (number) => {
        console.log(number)
        // Axios.post('/api/phone/makeCall', {
        //     number: number 
        // })
    }

    render(){
        return(
        <div style={style}>
            <Button onClick={()=>{this.toggleModal()}} style={{width: '100%', height: '100%', fontSize: '120px', marginTop: '-5px', color: '#110052'}}>
                <MdPhoneForwarded></MdPhoneForwarded>
            </Button>
            <p style={{fontSize: '20px', marginTop: '5px'}}><strong>Emergency Contacts</strong></p>
            <Modal shouldOpen={this.state.openContacts} title="Emergency Contacts">
                <div style={{width: '100%', height: '70%', marginTop: '5%', textAlign: 'center', justifyContent: 'center', overflow: 'scroll'}}>
                {this.state.contacts.map((d, i) => {
                    if(d.isActive){
                        let number = d.number
                        return <Button key={i} onClick={() => {(number) => {this.makeCall(number)}}} style={{fontSize:'20px'}}>{d.name}</Button>
                    }
                })}
                </div>
                <Button onClick={() => {this.toggleModal()}}>Cancel</Button>
            </Modal>
        </div>
        )}
}

export default emergencySection
