import React, { Component } from 'react'
import Button from '@material-ui/core/Button'

import Axios from 'axios'

const classes = {
    returnArrow: {
        fontSize: '40px',
        position: 'absolute',
        marginTop: '30px'
    },
    buttonsArea: {
        width: '80%',
        overflow: 'scroll',
        height: '80px',
        marginTop: '50px'
    },
    canvasArea: {
        width: '80%',
        height: '400px',
        display: 'flex',
        backgroundColor: '#333',
        textAlign: 'center',
        justifyContent: 'center',
        marginTop: '10px'
    },
    canvas: {
        backgroundColor: 'yellow',
        width: '80%'
    },
    buttonStyling: {
        marginLeft: '20px',
        marginRight: '20px'
    }
}

class LprConfig extends Component{

    state = {
        currentCamera: null,
        camerasList: null ,
        videoSource: null 
    }

    componentDidMount(){
        Axios.get('/api/cameras/getCameras')
        .then(data => {
            this.setState({camerasList: data.data, currentCamera: data.data[0]})
            console.log(data)
        }).catch(err => {
            console.log(err)
        })
    }

    render(){

        let buttons = this.state.camerasList ? this.state.camerasList.map((data, index) => {
            return data.isActive ? <Button style={classes.buttonStyling} key={index}>{data.name}</Button> : null
        }) : null 

        return(
            <div>
                <h1>Configure License Plate Detection Zone</h1>
                <p>Select a camera feed to configure its license plate detection area. Only license plates within the desingated box will be detected.</p>
                <div style={classes.buttonsArea}>
                { 
                    buttons
                }
                </div>
                <div style={classes.canvasArea}>
                    <canvas style={classes.canvas}>

                    </canvas>
                </div>
                <a href="/dashboard/configuration" style={{textDecoration: 'none', color: '#000'}}>
                    <span class="material-icons" style={classes.returnArrow}>
                        arrow_back
                    </span>  
                </a>           
            </div>
        )
    }

}

export default LprConfig
