import React, {useState, Component} from 'react';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button'
import Modal from '../popups/modal'
import Snack from '../snackbar/snackbar'

const Axios = require('axios')

const useStyles = makeStyles(theme => ({
  root: {
    left: 0,
    zIndex: '100',
    height: 500,
    position: 'relative',
    width: '100%',
    flexGrow: 1,
    minWidth: 300,
    transform: 'translateZ(0)',
    // The position fixed scoping doesn't work in IE 11.
    // Disable this demo to preserve the others.
    '@media all and (-ms-high-contrast: none)': {
      display: 'none',
    },
  },
  modal: {
    display: 'flex',
    padding: theme.spacing(1),
    alignItems: 'center',
    justifyContent: 'center',
  },
  paper: {
    width: 900,
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
  form:{
      padding: '15px',
      lineHeight: '20px',
      display: 'table'
  },
  label:{
      marginBottom: '10px',
  }
}));

class RegisterModal extends Component{

  state = {
    parentName: '',
    studentName: '',
    plate: '',
    confirmPlate: '',
    showModal: false,
    showRegError: false,
    showSuccess: false 
  }

  showModal = () => {
    if(this.validateRegistry()){
      console.log("Modal show")
      this.setState({showModal: true})
      this.setState({showRegError: false})
    }
    else{
      console.log("show error message")
      this.setState({showRegError: true})
    }
  }

  submitHandler = () => {

        if(this.validateRegistry()){
          this.setState({showRegError: false})
          let submit = Axios.post('/api/registration/plateRegistration',{
            parentName: this.state.parentName,
            studentName: this.state.studentName,
            plateNumber: this.state.plate,
            isActive: true 
          }).catch((e) => console.log(e))

          console.log(submit)
        }
        console.log("User added")
        this.setState({showSuccess: true})
        this.setState({showModal: false, parentName: '', studentName: '', plate: '', confirmPlate: ''})



    } 

    parentNameChangeHandler = (event) => {
        this.setState({parentName: event.target.value}) 
    }

    studentNameChangeHandler = (event) => {
      this.setState({studentName: event.target.value})
    }

    plateChangeHandler = (event) => {
      this.setState({plate: event.target.value}) 
    }

    confirmPlateChangeHandler = (event) => {
      this.setState({confirmPlate: event.target.value})
    }

    validateRegistry = () => {
        return((this.state.plate === this.state.confirmPlate) & (this.state.parentName !== "") & (this.state.studentName !== "") & (this.state.plate !== ""))
    }

    cancelHandler = () => {
      this.setState({showModal: false})
    }

  render(){

  let errorSnack = this.state.showRegError ? <Snack activate={this.state.showRegError} message="Please enter valid registration"></Snack> : null
  let successSnack = this.state.showSuccess ? <Snack activate={true} message="Plate successfully added!"></Snack> : null

  let cellStyle = {
    marginTop: '20px',
    width: '200px'
  }

  const {classes} = this.props;

  return (
    <div>
      <div>
          <h2 id="server-modal-title">Register a new license plate</h2>
        <form style={{display: 'grid'}}>
        <label className = {classes.label} style={cellStyle}>
            <TextField
            value = {this.state.parentName}
            id="1"
            label="Parent Name"
            variant="outlined"
            color="secondary"
            onChange = {this.parentNameChangeHandler}
            />
        </label>
        <label className = {classes.label} style={cellStyle}>
            <TextField
                value = {this.state.studentName}
                id="2"
                label="Student Name"
                variant="outlined"
                color="secondary"
                onChange = {this.studentNameChangeHandler}
            />
        </label>
        <label className = {classes.label} style={cellStyle}>
            <TextField
                value = {this.state.plate}
                id="3"
                label="Plate number"
                variant="outlined"
                color="secondary"
                onChange = {this.plateChangeHandler}
            />
        </label>
        <label className = {classes.label} style={cellStyle}>
            <TextField
                value = {this.state.confirmPlate}
                id="4"
                label="Confirm plate number"
                variant="outlined"
                color="secondary"
                onChange = {this.confirmPlateChangeHandler}
            />
        </label>
        <label className = {classes.label} style={cellStyle}>
            <Button variant = "outlined" onClick = {this.showModal}>
                Add user
            </Button>
        </label>
        </form>
        <Modal 
          shouldOpen={this.state.showModal}
          title="Confirm Registry">
            <p>Parent Name: {this.state.parentName}</p>
            <p>Student Name: {this.state.studentName}</p>
            <p>Plate Number: {this.state.plate}</p>
            <div>
              <Button variant = "outlined" onClick = {this.submitHandler} style={{marginTop: '20px'}}>
                  Confirm Add user
              </Button>
              <Button variant = "outlined" onClick = {this.cancelHandler} style={{marginTop: '20px', marginLeft: '20px'}}>
                  Cancel
              </Button>
            </div>
        </Modal>
        {errorSnack}
        {successSnack}
        </div>
    </div>
  )};
}

export default withStyles(useStyles)(RegisterModal)
