import React, {useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import TextField from '@material-ui/core/TextField';

import Button from '@material-ui/core/Button'

const useStyles = makeStyles(theme => ({
  root: {
    height: 500,
    flexGrow: 1,
    minWidth: 300,
    transform: 'translateZ(0)',
    // The position fixed scoping doesn't work in IE 11.
    // Disable this demo to preserve the others.
    '@media all and (-ms-high-contrast: none)': {
      display: 'none',
    },
  },
  modal: {
    display: 'flex',
    padding: theme.spacing(1),
    alignItems: 'center',
    justifyContent: 'center',
  },
  paper: {
    width: 900,
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
  form:{
      padding: '15px',
      display: 'grid',
      lineHeight: '20px'
  },
  label:{
      marginBottom: '10px',
  }
}));

export default function RegisterModal() {
  const classes = useStyles();
  const rootRef = React.useRef(null);

  const [parentName, studentName, plate, confirmPlate] = useState('')

    let submitHandler = () => {
        console.log(parentName, studentName, plate, confirmPlate)
    } 

    let parentNameChangeHandler = (event) => {
        parentName = event.target.value 
    }

    let studentNameChangeHandler = (event) => {
        studentName = event.target.value 
    }

    let plateChangeHandler = (event) => {
        plate = event.target.value 
    }

    let confirmPlateChangeHandler = (event) => {
        confirmPlate = event.target.value
    }

  return (
    <div className={classes.root} ref={rootRef}>
      <Modal
        disablePortal
        disableEnforceFocus
        disableAutoFocus
        open
        aria-labelledby="server-modal-title"
        aria-describedby="server-modal-description"
        className={classes.modal}
        container={() => rootRef.current}
      >
        <div className={classes.paper}>
          <h2 id="server-modal-title">Register a new license plate</h2>
          <form className={classes.form}>
        <label className = {classes.label}>
            <TextField
            id="outlined-secondary"
            label="Parent Name"
            variant="outlined"
            color="secondary"
            onChange = {(event) => {parentNameChangeHandler()}}
            />
        </label>
        <label className = {classes.label}>
            <TextField
                id="outlined-secondary"
                label="Student Name"
                variant="outlined"
                color="secondary"
                onChange = {(event) => {studentNameChangeHandler()}}
            />
        </label>
        <label className = {classes.label}>
            <TextField
                id="outlined-secondary"
                label="Plate number"
                variant="outlined"
                color="secondary"
                onChange = {(event) => {plateChangeHandler()}}
            />
        </label>
        <label className = {classes.label}>
            <TextField
                id="outlined-secondary"
                label="Confirm plate number"
                variant="outlined"
                color="secondary"
                onChange = {(event) => {confirmPlateChangeHandler()}}
            />
        </label>
        <label className = {classes.label}>
            <Button variant = "outlined" onClick = {submitHandler}>
                Add user
            </Button>
        </label>
        </form>
        </div>
      </Modal>
    </div>
  );
}
