import React, {useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button'
import Modal from '../popups/modal'

const Axios = require('axios')

const useStyles = makeStyles(theme => ({
  root: {
    left: 0,
    zIndex: '100',
    height: 500,
    position: 'relative',
    width: '100%',
    flexGrow: 1,
    minWidth: 300,
    transform: 'translateZ(0)',
    // The position fixed scoping doesn't work in IE 11.
    // Disable this demo to preserve the others.
    '@media all and (-ms-high-contrast: none)': {
      display: 'none',
    },
  },
  modal: {
    display: 'flex',
    padding: theme.spacing(1),
    alignItems: 'center',
    justifyContent: 'center',
  },
  paper: {
    width: 900,
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
  form:{
      padding: '15px',
      display: 'grid',
      lineHeight: '20px'
  },
  label:{
      marginBottom: '10px',
  }
}));

export default function RegisterModal() {
  const classes = useStyles();
  const rootRef = React.useRef(null);

  let [parentName, studentName, plate, confirmPlate] = useState('')
  let modal;

  let showModal = () => {
    console.log("Modal show")
    if(validateRegistry()){

    }
  }

  let submitHandler = async() => {

        if(validateRegistry()){
          let submit = await Axios.post('/api/registration/plateRegistration',{
            parentName: parentName,
            studentName: studentName,
            plateNumber: plate,
            isActive: true 
          }).catch((e) => console.log(e))

          console.log(submit)
        }
        else{
          console.log("Invalid register data")
        }
    } 

    let parentNameChangeHandler = (event) => {
        parentName = event.target.value 
    }

    let studentNameChangeHandler = (event) => {
        studentName = event.target.value 
    }

    let plateChangeHandler = (event) => {
        plate = event.target.value 
    }

    let confirmPlateChangeHandler = (event) => {
        confirmPlate = event.target.value
    }

    let validateRegistry = () => {
        return((plate === confirmPlate) & (parentName !== "") & (studentName !== "") & (plate !== ""))
    }

  return (
    <div  ref={rootRef}>
      <div >
          <h2 id="server-modal-title">Register a new license plate</h2>
          <form className={classes.form}>
        <label className = {classes.label}>
            <TextField
            id="1"
            label="Parent Name"
            variant="outlined"
            color="secondary"
            onChange = {parentNameChangeHandler}
            />
        </label>
        <label className = {classes.label}>
            <TextField
                id="2"
                label="Student Name"
                variant="outlined"
                color="secondary"
                onChange = {studentNameChangeHandler}
            />
        </label>
        <label className = {classes.label}>
            <TextField
                id="3"
                label="Plate number"
                variant="outlined"
                color="secondary"
                onChange = {plateChangeHandler}
            />
        </label>
        <label className = {classes.label}>
            <TextField
                id="4"
                label="Confirm plate number"
                variant="outlined"
                color="secondary"
                onChange = {confirmPlateChangeHandler}
            />
        </label>
        <label className = {classes.label}>
            <Button variant = "outlined" onClick = {showModal}>
                Add user
            </Button>
        </label>
        </form>
        <Modal 
          shouldOpen={true}
          title="Confirm Registry">
            <p>Parent Name: </p>
            <p>Student Name: </p>
            <p>Plate Number: </p>
            <Button variant = "outlined" onClick = {showModal}>
                Confirm Add user
            </Button>
        </Modal>
        </div>
    </div>
  );
}
