import React, { Component } from 'react'
import Button from '@material-ui/core/Button'
import Modal from '../popups/modal'

class configurationBoard extends Component {

    state = {
        showCameraModal: false,
        showContactsModal: false  
    }

    handleCameraModal = () => {
        let currentCamState = this.state.showCameraModal
        this.setState({showCameraModal: !currentCamState})
    }

    handleContactModal = () => {
        this.setState({showContactsModal: true})
    }

    render(){

    return(
        <div>
            <h1>Configuration</h1>
            <div>
                <div>
                    <Button variant="outlined" color="primary" href="#outlined-buttons" onClick={() => {this.handleCameraModal()}}>
                        Configure Cameras
                    </Button>
                    <Button variant="outlined" color="primary" href="#outlined-buttons" onClick={() => {this.handleContactModal()}}>
                        Configure emergency contacts
                    </Button>
                    <Modal shouldOpen={this.state.showCameraModal} title="Current IP Cameras">
                        <Button onClick={() => {this.handleCameraModal()}} style={{marginBottom: '0px'}}>Cancel</Button>
                    </Modal>
                    <Modal shouldOpen={this.state.showContactsModal} title="Configure Emergency Contacts">        
                    </Modal>
                </div>
            </div>
        </div>
    )}
}

export default configurationBoard
