import React, { Component } from 'react';
import clsx from 'clsx';
import { makeStyles, createMuiTheme } from '@material-ui/core/styles';
import CssBaseline from '@material-ui/core/CssBaseline';
import Drawer from '@material-ui/core/Drawer';
import Box from '@material-ui/core/Box';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import List from '@material-ui/core/List';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import Badge from '@material-ui/core/Badge';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Link from '@material-ui/core/Link';
import MenuIcon from '@material-ui/icons/Menu';
import {ThemeProvider, withStyles} from '@material-ui/core/styles'
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import NotificationsIcon from '@material-ui/icons/Notifications';
import { mainListItems, secondaryListItems } from './dashboardElements/listItems';
import UnregisteredFeed from './dashboardElements/unregisteredFeed'
import Emergency from './dashboardElements/emergency'
import SchoolHeader from './dashboardElements/schoolHeader'

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright © '}
      <Link color="inherit" href="https://material-ui.com/">
        SecureVision
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const drawerWidth = 240;

const useStyles = makeStyles(theme => ({
  root: {
    display: 'flex'
  },
  toolbar: {
    paddingRight: 24, // keep right padding when drawer closed
  },
  toolbarIcon: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-end',
    padding: '0 8px',
    ...theme.mixins.toolbar,
  },
  appBar: {
    backgroundColor: '#FFC80A',
    color: '#333',
    zIndex: theme.zIndex.drawer + 1,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },
  appBarShift: {
    marginLeft: drawerWidth,
    width: `calc(100% - ${drawerWidth}px)`,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  menuButton: {
    marginRight: 36,
  },
  menuButtonHidden: {
    display: 'none',
  },
  title: {
    flexGrow: 1,
  },
  drawerPaper: {
    backgroundColor: '#FFC80A',
    position: 'relative',
    whiteSpace: 'nowrap',
    width: drawerWidth,
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  drawerPaperClose: {
    overflowX: 'hidden',
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    width: theme.spacing(7),
    [theme.breakpoints.up('sm')]: {
      width: theme.spacing(9),
    },
  },
  appBarSpacer: theme.mixins.toolbar,
  content: {
    flexGrow: 1,
    height: '100vh',
    overflow: 'auto',
  },
  container: {
    paddingTop: theme.spacing(4),
    paddingBottom: theme.spacing(4),
  },
  paper: {
    padding: theme.spacing(2),
    display: 'flex',
    overflow: 'auto',
    flexDirection: 'column',
  },
  fixedHeight: {
    height: 240,
  },
}));

class Dashboard extends Component {

  feedToShow = null

  currPath = this.props.location.pathname

  // openState = [open, setOpen] = React.useState(true);

  open = React.useState(true)
  setOpen = React.useState(true)

  classes = useStyles();

  handleDrawerOpen = () => {
    this.setOpen(true);
  };
  handleDrawerClose = () => {
    this.setOpen(false);
  };
  fixedHeightPaper = clsx(this.classes.paper, this.classes.fixedHeight);

  render(){

    let defaultFeed = (
      <Container maxWidth="lg" className={this.classes.container}>
            <Grid container spacing={3}>
              {/* div area */}
              <Grid item xs={12} md={8} lg={9}>
                <Paper className={this.fixedHeightPaper}>
                  <SchoolHeader 
                    school="Boston College"
                    location="Chestnut Hill, MA"
                    />
                </Paper>
              </Grid>
              {/* Recent Deposits */}
              <Grid item xs={12} md={4} lg={3}>
                <Paper className={this.fixedHeightPaper}>
                  {<Emergency />}
                </Paper>
              </Grid>
              {/* Recent Orders */}
              <Grid item xs={12}>
                <Paper className={this.classes.paper}>
                  {<UnregisteredFeed />}
                </Paper>
              </Grid>
            </Grid>
            <Box pt={4}>
              <Copyright />
            </Box>
          </Container>
    )

    let dailyFeed = (
      <Container maxWidth="lg" className={this.classes.container}>
        <h1>Daily Activity</h1>
      </Container>
    )

    let unrecognizedFeed = (
      <Container maxWidth="lg" className={this.classes.container}>
        <h1>Unrecognized Activity</h1>
      </Container>
    )

    let cameraFeed = (
      <Container maxWidth="lg" className={this.classes.container}>
        <h1>Camera Feed</h1>
      </Container>
    )

    let newUserFeed = (
      <Container maxWidth="lg" className={this.classes.container}>
        <h1>Register a new user</h1>
      </Container>
    )

    let settingsFeed = (
      <Container maxWidth="lg" className={this.classes.container}>
        <h1>Configure Settings</h1>
      </Container>
    )

    let renderPage = () => {switch(this.currPath){
      case '/dashboard/activity':
        this.feedToShow = this.dailyFeed
        break
      case '/dashboard/unrecognized':
        this.feedToShow =  this.unrecognizedFeed
        break
      case '/dashboard/cameras':
        this.feedToShow = this.cameraFeed
        break 
      case '/dashboard/registerNewUser':
        this.feedToShow =  this.newUserFeed
        break 
      case '/dashboard/settings':
        this.feedToShow = this.settingsFeed
        break
      default:
        this.feedToShow = this.defaultFeed
        break 
    }
    return this.feedToShow
  }

    const {classes} = this.props

    const darkTheme = createMuiTheme({
      palette: {
        type: 'dark'
      }
    })

    return (
      <div className={classes.root}>
        <ThemeProvider theme={darkTheme}>
        <CssBaseline />
        <AppBar position="absolute" className={clsx(classes.appBar, this.open && classes.appBarShift)}>
          <Toolbar className={classes.toolbar}>
            <IconButton
              edge="start"
              color="inherit"
              aria-label="open drawer"
              onClick={this.handleDrawerOpen}
              className={clsx(classes.menuButton, this.open && classes.menuButtonHidden)}
            >
              <MenuIcon />
            </IconButton>
            <Typography component="h1" variant="h6" color="inherit" noWrap className={classes.title}>
              SecureVision Dashboard
            </Typography>
            <IconButton color="inherit">
              <Badge badgeContent={4} color="secondary">
                <NotificationsIcon />
              </Badge>
            </IconButton>
          </Toolbar>
        </AppBar>
        <Drawer
          variant="permanent"
          classes={{
            paper: clsx(classes.drawerPaper, !this.open && classes.drawerPaperClose),
          }}
          open={this.open}
        >
          <div className={classes.toolbarIcon}>
            <IconButton onClick={this.handleDrawerClose}>
              <ChevronLeftIcon />
            </IconButton>
          </div>
          <Divider />
          <List>{mainListItems}</List>
          <Divider />
          <List>{secondaryListItems}</List>
        </Drawer>
        <main className={classes.content}>
          <div className={classes.appBarSpacer} />
          {this.renderPage()}
        </main>
        </ThemeProvider>
      </div>
    );
  }}

export default withStyles(useStyles)(Dashboard)
