import React, { Component } from 'react'
import Button from '@material-ui/core/Button'
import Modal from '../material/modal'

import CameraItem from './cameraItem'
import AddCameraModal from './addCameraModal'
import ContactItem from './contactItem'
import AddContactModal from './addContactModal'

const Axios = require('axios')

class configurationBoard extends Component {

    state = {
        triggerAddCameraModal: false,
        triggerAddContactModal: false,
        showCameraModal: false,
        showContactsModal: false,
        camerasList: [],
        contactsList: []
    }

    getCameras = async() => {
        let result = await Axios.get('/api/cameras/getCameras')
        console.log(result)
        this.setState({camerasList: result.data})
    }

    getContacts = async() => {
        let result = await Axios.get('/api/contacts/getContacts')
        console.log(result)
        this.setState({contactsList: result.data})
    }

    handleCameraModal = () => {
        let currentCamState = this.state.showCameraModal
        if(!currentCamState){
            this.getCameras()
        }
        this.setState({showCameraModal: !currentCamState})
    }

    handleContactModal = () => {
        let currentCamState = this.state.showContactsModal
        if(!currentCamState){
            this.getContacts()
        }
        this.setState({showContactsModal: !currentCamState})
    }

    promptLprConfig = () => {
        this.props.history.push('/lprConfig')
    }

    cameraOnClose = () => {
        this.setState({triggerAddCameraModal: false})
    }

    contactOnClose = () => {
        this.setState({triggerAddContactModal: false})
    }

    refresh = async() => {
        let cameras= await Axios.get('/api/cameras/getCameras')
        this.setState({camerasList: cameras.data})

        let contacts = await Axios.get('/api/contacts/getContacts')
        this.setState({contactsList: contacts.data})

        this.setState({triggerAddCameraModal: false, triggerAddContactModal: false})
    }

    render(){

    return(
        <div>
            <h2 style={{marginLeft: '15%'}}>Configuration Menu</h2>
            <div>
                <div>
                        <Button style={{width: '60%', marginBottom: '20px', fontSize: '18px', justifyContent: 'left', marginLeft: '15%'}} variant="outlined" color="#333" href="#outlined-buttons" onClick={() => {this.handleCameraModal()}}>
                            Configure Cameras
                        </Button>
                        <Button style={{width: '60%', marginBottom: '20px', fontSize: '18px', justifyContent: 'left', marginLeft: '15%'}} variant="outlined" color="#333" href="#outlined-buttons" onClick={() => {this.handleContactModal()}}>
                            Configure emergency contacts
                        </Button>
                        <Button style={{width: '60%', marginBottom: '20px', fontSize: '18px', justifyContent: 'left', marginLeft: '15%'}} variant="outlined" color="#333" href="#outlined-buttons" onClick={() => {this.promptLprConfig()}}>
                            Configure License Plate Recognition Area
                        </Button>
                        <Modal shouldOpen={this.state.showCameraModal} title="Current IP Cameras">
                            <div style={{width: '100%'}}>
                                <p style={{float: 'left', marginLeft:'0px'}}><strong>Source</strong></p>
                                <p style={{float: 'left', marginLeft:'130px'}}><strong>Name</strong></p>
                                <p style={{float: 'left', marginLeft:'95px'}}><strong>Port</strong></p>
                            </div>
                            <div style={{overflow: 'scroll', height: '55%', width: '100%'}}>
                                {this.state.camerasList.map((d, i) => {
                                    if(d.isActive){
                                        return <CameraItem refresh={this.refresh} id={d._id} key={i} source={d.source} name={d.name} port={d.port}/>
                                    }
                                })}
                            </div>
                            <div style={{marginTop: '10px'}}>
                                <Button onClick={() => {this.setState({triggerAddCameraModal: true})}}>Add a new camera</Button>
                                <Button onClick={() => {this.handleCameraModal()}}>Cancel</Button>
                                <AddCameraModal open={this.state.triggerAddCameraModal} onClose={this.cameraOnClose}/>
                            </div>
                        </Modal>
                        <Modal shouldOpen={this.state.showContactsModal} title="Configure Emergency Contacts">
                        <div style={{width: '100%'}}>
                                <p style={{float: 'left', marginLeft:'0px'}}><strong>Phone Number</strong></p>
                                <p style={{float: 'left', marginLeft:'60px'}}><strong>Contact Name</strong></p>
                            </div>
                            <div style={{overflow: 'scroll', height: '55%', width: '100%'}}>
                                {this.state.contactsList.map((d, i) => {
                                    if(d.isActive){
                                        return <ContactItem refresh={this.refresh} id={d._id} key={i} number={d.number} name={d.name}/>
                                    }
                                })}
                            </div>
                            <div style={{marginTop: '10px'}}>
                                <Button onClick={()=>{this.setState({triggerAddContactModal: true})}}>Add Contact</Button>
                                <Button onClick={() => {this.handleContactModal()}} style={{marginTop: '10px'}}>Cancel</Button>
                                <AddContactModal open={this.state.triggerAddContactModal} onClose={this.contactOnClose}/>
                            </div>
                        </Modal>
                </div>
            </div>
        </div>
    )}
}

export default configurationBoard
