import Paper from '@material-ui/core/Paper'
import { Button } from '@material-ui/core'
const cocoSsd = require('@tensorflow-models/coco-ssd')
const videoSrc = require('./plateTest.mp4')

class MainFeed extends Component{

    videoRef = React.createRef();
    canvasRef = React.createRef();

    finished = false 

  showDetections = (predictions, video) => {
    const ctx = this.canvasRef.current.getContext("2d");

    let vid = this.videoRef.current
    vid.onended = () => {
      this.finished = true 
      alert("video has ended")
    }

    ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height);
    const font = "24px helvetica";
    ctx.font = font;
    ctx.textBaseline = "top";
    predictions.forEach(prediction => {

    if(this.finished){
      return 
    }

      console.log(prediction)

    //   if(prediction.class === 'car' || prediction.class === 'motorcycle' || prediction.class === 'truck' || prediction.class === 'bus'){
    //   const x = prediction.bbox[0];
    //   const y = prediction.bbox[1];
    //   const width = prediction.bbox[2];
    //   const height = prediction.bbox[3];
    //   // Draw the bounding box.
    //   ctx.strokeStyle = "#FF7801";
    //   ctx.lineWidth = 1;
    //   ctx.strokeRect(x, y, width, height);
    //   // Draw the label background.
    //   ctx.fillStyle = "#FF7801";
    //   const textWidth = ctx.measureText(prediction.class).width;
