    //   // draw top left rectangle
    //   ctx.fillRect(x, y, textWidth + 10, textHeight + 10);
    //   // draw bottom left rectangle
    //   ctx.fillRect(x, y + height - textHeight, textWidth + 15, textHeight + 10);

    //   ctx.fillStyle = "#000000";
    //   ctx.fillText(prediction.class, x, y);
    //   ctx.fillText(prediction.score.toFixed(2), x, y + height - textHeight);

      let tempCanvas = document.createElement('canvas')
      let tempContext = tempCanvas.getContext('2d')
      tempContext.drawImage(video, 0, 0, tempCanvas.width, tempCanvas.height)
      let imageData = tempContext.getImageData(x, y, width, height)

      // let contrastData = this.imageContrast(imageData, 120)
      // let grayScaleData = this.imageGrayScale(contrastData, tempCanvas.width, tempCanvas.height)
      // let thresholdData = this.imageThreshold(grayScaleData, 170)  
      tempContext.putImageData(imageData, 0, 0)

      let currentCar = new Image()
      currentCar.src = tempCanvas.toDataURL('image/png')
      document.body.append(currentCar)
      // this.characterRecognize(currentCar)

    });
  };

    detectFromVideoFrame = (model, video) => {
        model.detect(video).then(predictions => {
          this.showDetections(predictions, video);
          requestAnimationFrame(() => {
            this.detectFromVideoFrame(model, video);
          });
        }, (error) => {
          console.error(error)
        });
      };

    state = {
        source: this.props.source,
        name: this.props.name,
        port: this.props.port,
        buttonNames: this.props.buttonNames
    }  

    componentDidMount(){
        const loadlModelPromise = cocoSsd.load();

      // resolve all the Promises
        Promise.all([loadlModelPromise])
            .then(values => {
            this.detectFromVideoFrame(values[0], this.videoRef.current);
            })
            .catch(error => {
            console.error(error);
            });
    }

    render(){

        let player = {
            marginTop: '5px'
        }

        let buttonGridStyle={
            width: '700px',
            height: '200px',
            textAlign:'center'
        }

        let buttons = {
            marginLeft: '15px', 
            width: '670px', 
            height:'180px', 
            display: 'grid',
            gridTemplateColumns: 'repeat(6, 111.66px)'
        }

        const styles = {
            position: 'fixed',
            top: 150,
            left: 150,
          };

        return(
            <div>
                <div style={{width: '700px', height: '420px', backgroundColor: '#333'}}>
                    {/* <ReactPlayer url={'https://www.youtube.com/watch?v=F4SY7kTpqjk'} playing muted controls={false} className={player} width="100%" height="100%"/> */}
