                    <canvas style={styles} ref={this.canvasRef} width="720" height="650" />
                    <div style={{marginTop: '15px'}}>
                        <h1 style={{marginLeft: '15px'}}>{this.state.name}</h1>
                    </div>
                    <div style={buttonGridStyle}>
                        <div style={buttons}>
                            {this.state.buttonNames.map((d) => {
                                console.log(d)
                             return <Button>{d}</Button>
                            })}
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default MainFeed
