import React, {Component} from 'react';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Paper from '@material-ui/core/Paper';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import { withStyles } from '@material-ui/core/styles';
import NavigationItem from '../navigationItem/navigationItem'
const Axios = require('axios').default

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright Â© '}
      <Link color="inherit" href="https://material-ui.com/">
        Secure Vision
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const useStyles = (theme => ({
  root: {
    height: '100vh',
  },
  image: {
    backgroundImage: 'url(https://images.pexels.com/photos/159823/kids-girl-pencil-drawing-159823.jpeg?cs=srgb&dl=girls-on-desk-looking-at-notebook-159823.jpg&fm=jpg)',
    backgroundRepeat: 'no-repeat',
    backgroundColor:
      theme.palette.type === 'dark' ? theme.palette.grey[900] : theme.palette.grey[50],
    backgroundSize: 'cover',
    backgroundPosition: 'center',
  },
  paper: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#FFC80A',
    height: '100%',
    width: '100%'
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: '#0A85FF',
  },
  form: {
    width: '80%', // Fix IE 11 issue.
    marginTop: '5%'
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
    backgroundColor: '#0A85FF'
  },
  title:{
      fontSize: '70px',
      color: "fff",
      marginTop: '-20%',
  },
  header:{
      lineHeight: '25px',
      alignItems: 'center',
      textAlign: 'center',
  }
}));

 class SignInSide extends Component {

  state = {
    canSubmit: false,
    email: '',
    password: ''
  }

  // submitHandler = async () => {
  //     console.log(this.state)
  //     await Axios.post('/login', {
  //       email:this.state.email,
  //       password:this.state.password
  //     })
  // }

  submitHandler = async () => {
    Axios.get('/api').then((response) => {
      console.log(response)
      console.log('hello world')
    })
  } 

  emailChangeHandler = (event) => {
    let newState = {...this.state}
    let email = event.target.value 
    console.log(email)
    newState.email = email 
    this.setState(newState)
  }

  passwordChangeHandler = (event) => {
    let newState = {...this.state}
    let password = event.target.value
    console.log(password)
    newState.password = password 
    this.setState(newState)
  }

  render(){

  const {classes} = this.props;

  return (
    <Grid container component="main" className={classes.root}>
      <CssBaseline/>
      <Grid item xs={false} sm={4} md={7} className={classes.image} />
      <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
        <div className={classes.paper}>
            <div className={classes.header}>
                <h1 className={classes.title}>Secure Vision</h1>
                <h2>The next step in keeping your children safe</h2>
            </div>
          {/* <Avatar className={classes.avatar}>
            <img 
            src = 'https://assets.website-files.com/5d73650234bd36e04cdf6ef6/5d73999e7149d5e6c6002654_logo.svg'
            style = {{width: '75%'}}
            >
            </img>
          </Avatar> */}
          {/* <Typography component="h1" variant="h5">
            Sign in
          </Typography> */}
          <form className={classes.form} noValidate>
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              id="email"
              label="Email Address"
              name="email"
              autoComplete="email"
              autoFocus
              onChange={this.emailChangeHandler}
            />
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              name="password"
              label="Password"
              type="password"
              id="password"
              autoComplete="current-password"
              onChange={this.passwordChangeHandler}
            />
            <FormControlLabel
              control={<Checkbox value="remember" color="primary" />}
              label="Remember me"
            />
            <NavigationItem link="/dashboard" exact={true}>
                <Button
                type="submit"
                fullWidth
                variant="contained"
                color="primary"
                className={classes.submit}
                onClick = {() => this.submitHandler()} 
                >
                Sign In
                </Button>
            </NavigationItem>
            <Grid container>
                <Grid item xs>
                  <Link href="/forgotPassword" variant="body2">
                    Forgot password?
                  </Link>
                </Grid>
              <Grid item>
                <Link href="/register" variant="body2">
                  {"Register your school now"}
                </Link>
              </Grid>
            </Grid>
            <Box mt={5}>
              <Copyright />
            </Box>
          </form>
        </div>
      </Grid>
    </Grid>
  )};
}

export default withStyles(useStyles)(SignInSide)
