import React, { Component } from 'react'
import FeedItem from './plateFeedItem'
import Axios from 'axios'
import SearchBar from '../AutoComplete/search'
import { TextField } from '@material-ui/core'
// import Search from './autoCompSearch'

let FeedStyle = {
   overflow: 'scroll'
}

class platesFeed extends Component {

    state = {
        plateCollection: [],
        feed: [],
        searchVal: ''
    }

    getRegisterData  = async() => { 
        let getData =  await Axios.get('/api/get/getPlates')

        let plateData = getData.data
        console.log("Plate list:", plateData)

        this.setState({plateCollection: plateData})
    }

    updateSearchVal = (event) => {
        this.setState({searchVal: event.target.value})
        this.state.feed.map((plate) => {
            return plate
        })
    }

    updateFeedList = async() => {
        console.log(this.state.plateCollection)
        let feed = []
        this.state.plateCollection.map((plate, i) => {
            if(plate.isActive){
            feed.push(<FeedItem
                plateNumber = {plate.plateNumber}
                studentName = {plate.studentName}
                parentName = {plate.parentName}
                id = {plate._id}
                key = {i} 
            />)}
        })
        this.setState({feed: feed})
    }

    async componentDidMount(){
        await this.getRegisterData()
        await this.updateFeedList()
        console.log(this.state, 'component mounted')
    }

    render(){
        let filteredFeed = this.state.feed.filter((plate) => {
            return this.state.feed.indexOf(this.state.searchVal) !== -1
        })
        console.log()
        return(
            <div>
                <h1>Active Plates</h1>
                <TextField variant="outlined" onChange={this.updateSearchVal}></TextField>
                <div style={FeedStyle}>
                    {this.state.feed}
                </div>
            </div>
        )
    }
}

export default platesFeed
