import React, { Component } from 'react';
import { withStyles, makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import { Button } from '@material-ui/core';

import ParentModalFlow from './parentsModal'

const Axios = require('axios')

const StyledTableCell = withStyles((theme) => ({
  head: {
    backgroundColor: '#333',
    color: '#E5E5E5',
    fontSize: 17
  },
  body: {
    fontSize: 15,
  },
}))(TableCell);

const StyledTableRow = withStyles((theme) => ({
  root: {
      backgroundColor: '#E5E5E5',
  },
}))(TableRow);

let rows = [
    createData('Alec Lawlor', 'Parent', '123456', [<Button>Edit</Button>,<Button>Delete</Button>])
]

function createData(name, type, platenumber, actions) {
  return { name, type, platenumber, actions };
}

console.log(rows)

const useStyles = makeStyles({
  table: {
    minWidth: 700,
    maxHeight: '200px'
  },
});

class CustomizedTables extends Component {

  state = {
      data: [],
      showParentEdit: false,
      showParentDeactivate: false
  }


  async componentDidMount(){

    let getData =  await Axios.get('/api/get/getPlates')
    let getHighAlert = await Axios.get('/api/get/getHighRisk')

    let plateData = getData.data
    let highAlertData = getHighAlert.data

    // merge parent plate and highAlert plate data
    let mergedData = plateData.concat(highAlertData)
    console.log("Plate list:", mergedData)

    this.setState({data: plateData})
  }

  handleEditModal = () => {
      this.setState({showParentEdit: true})
  }

  handleDeleteModal = () => {
    this.setState({showParentDeactivate: false})
  }

  render(){
    const {classes} = this.props;
  return (
    <TableContainer component={Paper}>
      <Table className={classes.table} aria-label="customized table">
        <TableHead>
          <TableRow>
            <StyledTableCell>Name</StyledTableCell>
            <StyledTableCell align="left">Type</StyledTableCell>
            <StyledTableCell align="left">Plate Number</StyledTableCell>
            <StyledTableCell align="right">Actions</StyledTableCell>
          </TableRow>
        </TableHead>
        <TableBody style={{maxHeight: '200px', overflow: 'scroll'}}>
          {this.state.data.map((row, i) => {
            if(row.isActive){
                return(
            <StyledTableRow key={i}>
              <StyledTableCell component="th" scope="row">
                {row.parentName}
              </StyledTableCell>
              <StyledTableCell align="left">{row.studentName}</StyledTableCell>
              <StyledTableCell align="left">{row.plateNumber}</StyledTableCell>
              <StyledTableCell align="right">{[<Button onClick={(e) => {this.handleEditModal(e)}}>Edit</Button>,<Button onClick={(e)=>{this.handleDeleteModal(e)}}>Delete</Button>]}</StyledTableCell>
                </StyledTableRow>)}
  })}
        </TableBody>
      </Table>
      <ParentModalFlow showEditModal={this.state.showParentEdit} showDeactivateModal={this.state.showParentDeactivate}/>
    </TableContainer>
  )};
}

export default withStyles(useStyles)(CustomizedTables)
