import React, { Component } from 'react';
import { withStyles, makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import { Button, TextField } from '@material-ui/core';
import TableItem from './tableItem'
import SearchIcon from '@material-ui/icons/Search'

import ParentModalFlow from './parentsModal'

const Axios = require('axios')

const StyledTableCell = withStyles((theme) => ({
  head: {
    backgroundColor: '#333',
    color: '#E5E5E5',
    fontSize: 17
  },
  body: {
    fontSize: 15,
  },
}))(TableCell);

const StyledTableRow = withStyles((theme) => ({
  root: {
      backgroundColor: '#E5E5E5',
  },
}))(TableRow);

let rows = [
    createData('Alec Lawlor', 'Parent', '123456', [<Button>Edit</Button>,<Button>Delete</Button>])
]

function createData(name, type, platenumber, actions) {
  return { name, type, platenumber, actions };
}

console.log(rows)

const useStyles = makeStyles({
  table: {
    minWidth: 700,
    maxHeight: '200px'
  },
});

class CustomizedTables extends Component {

  state = {
      data: [],
      showParentEdit: false,
      showParentDeactivate: false,
      search: ''
  }


  async componentDidMount(){

    let getData =  await Axios.get('/api/get/getPlates')
    let getHighAlert = await Axios.get('/api/get/getHighRisk')

    let plateData = getData.data
    let highAlertData = getHighAlert.data

    // merge parent plate and highAlert plate data
    let mergedData = plateData.concat(highAlertData)
    console.log("Plate list:", mergedData)

    this.setState({data: mergedData})
  }

  onCancel = () => {
    this.setState({showParentEdit: false, showParentDeactivate: false})
  }

  submitted = async() => {
    this.setState({showParentEdit: false, showParentDeactivate: false})
    console.log("Hello World")

    let getData =  await Axios.get('/api/get/getPlates')
    let getHighAlert = await Axios.get('/api/get/getHighRisk')

    let plateData = getData.data
    let highAlertData = getHighAlert.data

    // merge parent plate and highAlert plate data
    let mergedData = plateData.concat(highAlertData)
    console.log("Plate list:", mergedData)

    this.setState({data: mergedData})
  }

  updateSearch = (event) => {
    this.setState({search: event.target.value.substr(0, 20)})
    console.log("Search")
  }

  render(){
  const {classes} = this.props;

  let filteredData = this.state.data.filter(
    (person) => {
      if(person.parentName){
        return person.parentName.indexOf(this.state.search !== -1)
      }
      else if(person.name){
        return person.name.indexOf(this.state.search !== -1)
      }
    }
  )

  console.log(filteredData)

  return (
  <div>
    <TextField onChange={this.updateSearch} label={<SearchIcon style={{fontSize: '24px'}}></SearchIcon>} variant="outlined" style={{color: '#333', width: '200px', height: '20px', marginBottom: '50px'}}></TextField>
    <TableContainer component={Paper}>
      <Table className={classes.table} aria-label="customized table">
        <TableHead>
          <TableRow>
            <StyledTableCell>Name</StyledTableCell>
            <StyledTableCell align="left">Type</StyledTableCell>
            <StyledTableCell align="left">Plate Number</StyledTableCell>
            <StyledTableCell align="right">Actions</StyledTableCell>
          </TableRow>
        </TableHead>
        <TableBody style={{maxHeight: '200px', overflow: 'scroll'}}>
          {filteredData.map((row, i) => {
            if(row.isActive){
                return <TableItem i={i} id={row._id} onSubmit={this.submitted} studentName={row.studentName} name={row.parentName ? row.parentName : row.name} type={row.parentName ? 'Parent': row.type} plateNumber={row.plateNumber}/>
              }
          })}
        </TableBody>
      </Table>
    </TableContainer>
    </div>
  )};
}

export default withStyles(useStyles)(CustomizedTables)
