import React, { Component } from 'react'
import Button from '@material-ui/core/Button'
import Modal from '../material/modal'
import TextField from '@material-ui/core/TextField'

const Axios = require('axios')

class CameraItem extends Component {

    state = {
        number: this.props.number,
        name: this.props.name,
        id: this.props.id,
        openEditModal: false,
        openDeactivateModal: false,
        editName: this.props.name,
        editNumber: this.props.number
    }

    handleEditModal = () => {
        this.setState({openEditModal: true})
    }

    handleDeactivateModal = () => {
        this.setState({openDeactivateModal: true})
    }

    close = () => {
        this.setState({openDeactivateModal: false, openEditModal: false})
    }

    editContact = async() => {
        const id = this.state.id
        let editItem = await Axios.put('/api/contacts/editContact', {
            id: id,
            name: this.state.editName,
            number: this.state.editNumber 
        })
        console.log(editItem)
        this.close()
    }

    nameChange = (e) => {
        this.setState({editName: e.target.value})
    }

    numberChange = (e) => {
        this.setState({editNumber: e.target.value})
    }

    removeContact = async() => {
        const id = this.state.id 
        console.log(id)

        let deactivate = await Axios.put('/api/contacts/deactivateContact', {
            id: id 
        })
        console.log(deactivate)
        this.close()
    }

    render(){

        let style ={
            width: '100%'
        }

        let btnStyle = {width: '100%', marginTop: '10px', marginBottom: '10px'}

        return(
            <div style={style}>
                <p style={{float: 'left', marginLeft: '0px', maxHeight: '20px', width:'90px',overflow: 'hidden'}}>{this.state.number}</p>
                <p style={{float: 'left', marginLeft: '70px', maxHeight: '20px', width:'150px', overflow: 'hidden'}}>{this.state.name}</p>
                <p style={{float: 'left', marginLeft: '65px', width: '60px',overflow: 'hidden'}}><Button onClick={this.handleEditModal}>edit</Button></p>
                <p style={{float: 'left', marginLeft: '10px', marginLeft: '15px', width: '80px', overflow: 'hidden'}}><Button onClick={this.handleDeactivateModal}>delete</Button></p>
                <Modal shouldOpen={this.state.openEditModal}>
                    <TextField label="Number" variant="outlined" onChange={(e) => this.numberChange(e)} value={this.state.editNumber}></TextField>
                    <TextField label="Name" variant="outlined" onChange={(e) => this.nameChange(e)} value={this.state.editName}></TextField>
                    <Button style={btnStyle} onClick={this.editContact}>Confirm Edit</Button>
                    <Button style={btnStyle} onClick={this.close}>Cancel</Button>
                </Modal>
                <Modal shouldOpen={this.state.openDeactivateModal}>
                    <p>Are you sure you would like to delete this contact? Deleting the contact remove it from your contacts list permanently</p>
                    <Button onClick = {this.removeContact}>Confirm Delete</Button>
                    <Button onClick={this.close}>Cancel</Button>
                </Modal>
            </div>
        )
    }
}

export default CameraItem
