import React, { Component } from 'react';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';

const Axios = require('axios')

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
  chart: {
      width: '125%',
      height: '300px'
  },
  heatmap: {
      padding: theme.spacing(2),
      width: '75%',
      marginLeft: '25%',
      height: '310px'
  },
  timePlot: {
    marginTop: '30px',
    padding: theme.spacing(2),
    width: '75%',
    marginLeft: '25%',
    height: '310px'
  }
}));

class MyGrid extends Component {

  intervalId

  state = {
    dataList: []
  }

  refresh = async() => {
    let data = await Axios.get('/api/lpr/getUnrecognizedData')

    let rawData = data.data
    let sorted = rawData.sort((a,b) => (a.date < b.date) ? 1: (a.date === b.date) ? ((a.time < b.time) ? 1 : -1): -1)

    this.setState({dataList: sorted})
  }

  componentDidMount(){
    this.refresh()
    this.intervalId = setInterval(this.refresh, 1000)
  }

  componentWillUnmount(){
    clearInterval(this.intervalId)
  }

  render(){
  const {classes} = this.props;

  return (
    <div className={classes.root}>
      <Grid container spacing={3}>
        <Grid item xs={6}>
          <Paper className={classes.chart} style={{width: '200%'}}>
              <UnrecognizedTable dataList={this.state.dataList}/>
          </Paper>
        </Grid>
        <Grid item xs={6}>
          {/* <Paper className={classes.heatmap} style={{backgroundColor: '#E5E5E5', width: '75%', height: '330px',marginLeft: '25%'}}>
              Chart showing the areas with the most unrecognized plate
          </Paper>
          <Paper className={classes.timePlot} style={{backgroundColor: '#E5E5E5', width: '75%', height: '330px',marginLeft: '25%', marginTop: '30px'}}>
              Chart showing activity over time
          </Paper> */}
        </Grid>
      </Grid>
    </div>
  )};
}

export default withStyles(useStyles)(MyGrid)
