import React, {Component} from 'react'
import TableFixedHeader from './completeLog'
import Snackbar from '../snackbar/snackbar'

const Axios = require('axios')

class activityLayout extends Component{

  intervalId

  state = {
    dataList: [],
    triggerAlert: false 
  }

  refresh = async() => {
    let data = await Axios.get('/api/lpr/getGeneralData')
    let rawData = data.data
    // console.log(rawData)
    let sorted = rawData.sort((a,b) => (a.date < b.date) ? 1: (a.date === b.date) ? ((a.time < b.time) ? 1 : -1): -1)

    let currentList = this.state.dataList
    // let newPlates = sorted.filter(item => currentList.includes(item))

    let triggerSnackBar = false
    if(currentList.length != sorted.length){
        triggerSnackBar = true
    }

    this.setState({dataList: sorted, shouldAlert: triggerSnackBar})
  }

  componentDidMount(){
    this.refresh()
    this.intervalId = setInterval(this.refresh, 3000)
  }

  componentWillMount(){
      clearInterval(this.intervalId)
  }


  render(){
    return(
        <div>
            <TableFixedHeader dataList={this.state.dataList}/>
        </div>
       )
    }
}

export default activityLayout
