import React, { useEffect, useState } from 'react';
import clsx from 'clsx';
import { makeStyles, createMuiTheme } from '@material-ui/core/styles';
import CssBaseline from '@material-ui/core/CssBaseline';
import Drawer from '@material-ui/core/Drawer';
import Box from '@material-ui/core/Box';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import List from '@material-ui/core/List';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import Badge from '@material-ui/core/Badge';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Link from '@material-ui/core/Link';
import MenuIcon from '@material-ui/icons/Menu';
import {ThemeProvider} from '@material-ui/core/styles'
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import { mainListItems, secondaryListItems } from './dashboardElements/listItems';
import Emergency from './dashboardElements/emergency'
import SchoolHeader from './dashboardElements/schoolHeader'
import UserRegister from '../registerNewPlate/registerUser'
import PlateFeed from '../activePlatesFeed/searchTable'
import ConfigFeed from '../configuration/configuration'
import CamFeed from '../cameraFeed/cameraLayout'
import DailyFeed from '../dailyActivity/activityLayout'
import UnrecongizedLayout from '../unrecognizedActivity/unrecognizedDashboard'
import Button from '@material-ui/core/Button'
import LprConfig from '../configuration/lprConfig'
import { MdPhoneForwarded } from 'react-icons/md'

const Axios = require('axios')
const logo = require('../../assets/images/logo0.svg')

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright Â© '}
      <Link color="inherit" href="https://material-ui.com/">
        SecureVision
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const drawerWidth = 240;

const useStyles = makeStyles(theme => ({
  root: {
    display: 'flex',
    zIndex: '1'
  },
  toolbar: {
    paddingRight: 24, // keep right padding when drawer closed
  },
  toolbarIcon: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-end',
    padding: '0 8px',
    ...theme.mixins.toolbar,
  },
  appBar: {
    backgroundColor: '#E5E5E5',
    color: '#110052',
    zIndex: theme.zIndex.drawer + 1,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },
  appBarShift: {
    marginLeft: drawerWidth,
    width: `calc(100% - ${drawerWidth}px)`,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  menuButton: {
    marginRight: 36,
  },
  menuButtonHidden: {
    display: 'none',
  },
  title: {
    flexGrow: 1,
  },
  drawerPaper: {
    backgroundColor: '#E5E5E5',
    position: 'relative',
    whiteSpace: 'nowrap',
    width: drawerWidth,
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  drawerPaperClose: {
    overflowX: 'hidden',
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    width: theme.spacing(7),
    [theme.breakpoints.up('sm')]: {
      width: theme.spacing(9),
    },
  },
  appBarSpacer: theme.mixins.toolbar,
  content: {
    flexGrow: 1,
    height: '100vh',
    overflow: 'auto',
    backgroundColor: '#E5E5E5'
  },
  container: {
    paddingTop: theme.spacing(4),
    paddingBottom: theme.spacing(4),
    backgroundColor: '#E5E5E5'
  },
  paper: {
    padding: theme.spacing(2),
    display: 'flex',
    overflow: 'auto',
    flexDirection: 'column',
    color: '#110052',
    backgroundColor: '#E5E5E5'
  },
  fixedHeight: {
    height: 240,
    backgroundColor: '#E5E5E5'
  },
}));


export default function Dashboard(props) {

  const registerLogout = () => {
    let logout = Axios.get('/api/auth/logout')
    console.log(logout)
    props.history.push('/')
  }

  let currPath = props.location.pathname

  const classes = useStyles();
  const [open, setOpen] = React.useState(true);
  const [toggleContactsModal, setContactsModal] = React.useState(false)
  const [mostRecentCriminalPlateObject, setMostRecentCriminalPlate] = React.useState([{}])
  const [mostRecentUnrecognizedPlateObject, setMostRecentUnrecognizedPlate] = React.useState([{}])

  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false);
  };

  const handleNewCriminalPlate = (plateObject) => {
    console.logO(plateObject)
    setMostRecentCriminalPlate(plateObject)
  }

  const handleNewUnrecognziedPlate = (plateObject) => {
    console.log([plateObject])
    setMostRecentUnrecognizedPlate([plateObject])
  }

  const handleContactsOpen = () => {
    console.log('modal open')
    setContactsModal(true)
  }

  const handleContactsClose = () => {
    console.log('modal close')
    setContactsModal(false)
  }

  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);

  let feedToShow

  let defaultFeed = (
    <Container maxWidth="lg" className={classes.container}>
      <Grid container spacing={3}>
        <Grid item xs={12} md={8} lg={9}>
          <Paper className={fixedHeightPaper}>
            <SchoolHeader 
              school="Boston College"
              location="Chestnut Hill, MA"/>
          </Paper>
        </Grid>
        <Grid item xs={12} md={4} lg={3}>
          <Paper className={fixedHeightPaper}>
            <Button onClick={()=>{handleContactsOpen()}} style={{width: '100%', height: '100%', fontSize: '120px', marginTop: '-5px', color: '#110052'}}>
                <MdPhoneForwarded></MdPhoneForwarded>
            </Button>
            <p style={{fontSize: '20px', marginTop: '5px'}}><strong>Emergency Contacts</strong></p>
          </Paper>
        </Grid>
        <Grid item xs={12}>
          <Paper className={classes.paper}>
            <h2>Last Unregistered Entry: {mostRecentUnrecognizedPlateObject}</h2>
            <h2>Last Criminal Entry: {mostRecentCriminalPlateObject}</h2>
          </Paper>
        </Grid>
    </Grid>
    <Box pt={4}>
    <Copyright />
    </Box>
  </Container>)

  let dailyFeed = (
    <Container maxWidth="lg" className={classes.container}>
      <DailyFeed openModal={handleContactsOpen} setMostRecent={handleNewCriminalPlate}/>
    </Container>
  )

  let unrecognizedFeed = (
    <Container maxWidth="lg" className={classes.container}>
      <UnrecongizedLayout setMostRecent={handleNewUnrecognziedPlate} />
    </Container>
  )

  let cameraFeed = (
    <Container maxWidth="lg" className={classes.container}>
      <CamFeed />
    </Container>
  )

  let newUserFeed = (
    <Container maxWidth="lg" className={classes.container}>
      <UserRegister></UserRegister>
    </Container>
  )

  let plateFeed = (
    <Container maxWidth="lg" className={classes.container}>
      <PlateFeed />
    </Container>
  )

  let configFeed = (
    <Container maxWidth="lg" className={classes.container}>
      <ConfigFeed />
    </Container>
  )

  let lprConfigFeed = (
    <Container maxWidth="lg" className={classes.container}>
      <LprConfig />
    </Container>
  )

  switch(currPath){
    case '/dashboard/activity':
      feedToShow = dailyFeed
      break
    case '/dashboard/unrecognized':
      feedToShow =  unrecognizedFeed
      break
    case '/dashboard/cameras':
      feedToShow = cameraFeed
      break 
    case '/dashboard/registerNewUser':
      feedToShow =  newUserFeed
      break 
    case '/dashboard/plates':
      feedToShow = plateFeed
      break
    case '/dashboard/configuration':
      feedToShow = configFeed
      break
    case '/dashboard/configuration/lprConfig':
      feedToShow = lprConfigFeed
      break
    default:
      feedToShow = defaultFeed
      break 
  }

  return (
    <div className={classes.root}>
      <CssBaseline />
      <AppBar position="absolute" className={clsx(classes.appBar, open && classes.appBarShift)}>
        <Toolbar className={classes.toolbar}>
          <IconButton
            edge="start"
            color="inherit"
            aria-label="open drawer"
            onClick={handleDrawerOpen}
            className={clsx(classes.menuButton, open && classes.menuButtonHidden)}>
            <MenuIcon />
          </IconButton>
          <Typography component="h1" variant="h6" color="inherit" noWrap className={classes.title}>
            <img src={logo} style={{height: '45px', width: 'auto', marginTop: '10px'}}></img>
          </Typography>
          <Button style={{marginRight: '30px'}}>
            Report a Problem
          </Button>
          <Button color="inherit" onClick = {registerLogout}>
            Logout
          </Button>
        </Toolbar>
      </AppBar>
      <Drawer
        variant="permanent"
        classes={{
          paper: clsx(classes.drawerPaper, !open && classes.drawerPaperClose),
        }}
        open={open}>
        <div className={classes.toolbarIcon}>
          <IconButton onClick={handleDrawerClose}>
            <ChevronLeftIcon />
          </IconButton>
        </div>
        <Divider />
        <List>{mainListItems}</List>
        <Divider />
        <List>{secondaryListItems}</List>
      </Drawer>
      <main className={classes.content}>
        <div className={classes.appBarSpacer} />
        {feedToShow}
        {<Emergency shouldOpen={toggleContactsModal} close={handleContactsClose}/>}
      </main>
    </div>
  );
}
