import React, { Component } from 'react'
import Modal from '../material/modal'
import Button from '@material-ui/core/Button'
import { TextField } from '@material-ui/core'

const Axios = require('axios')

class addCameraModal extends Component{

    state = {
        number: '',
        name: ''
    }

    handleFormSubmit = () => {

        const id = JSON.parse(localStorage.getItem('userObj'))._id

        let send = async() => {
            let req = await Axios.post('/api/contacts/addContacts', {
                number: this.state.number,
                name: this.state.name,
                id: id 
            })
        console.log(req)
    }
        send()
        this.setState({open: false})
        this.props.onClose()
    }

    updateNumber = (event) => {
        this.setState({number: event.target.value})
    }

    updateName = (event) => {
        this.setState({name: event.target.value})
    }

    triggerClose = () => {
        this.props.onClose()
    }

    render(){
        return(
            <Modal title="Register a new camera" shouldOpen={this.props.open}>
                <div style={{width: '50%', lineHeight: '65px'}}>
                    <TextField label="Phone Number" style={{width: '100%'}} variant="outlined" value={this.state.number} onChange={this.updateNumber}></TextField>
                    <TextField label="Name" style={{width: '100%'}} variant="outlined" value={this.state.name} onChange={this.updateName}></TextField>
                </div>
                <Button onClick = {() => {this.handleFormSubmit()}}>Submit</Button>
                <Button onClick = {() => {this.triggerClose()}}>Cancel</Button>
            </Modal>
        )
    }
}

export default addCameraModal
