import React, { Component } from 'react'
import ReactPlayer from 'react-player'
import { Button } from '@material-ui/core'

const Axios = require('axios')

class MainFeed extends Component{

  state = {
          source: this.props.source,
          name: this.props.name,
          port: this.props.port,
          buttonNames: this.props.buttonNames,
          finished: false,
          source: 'https://www.youtube.com/embed/OOPWzhFnCZg',
          header: 'Default Feed'
    }  

    handleChange = (name, source) => {
      this.setState({header: name, source: source})
    }

    componentDidMount(){

        const getCameras = async() => {

          const schoolID = JSON.parse(localStorage.getItem('userObj'))._id

          let result = await Axios.get('/api/cameras/getCameras')
          this.setState({buttonNames: result.data.map((d, i) => {
            if(d.isActive && d.schoolID === schoolID){
              return <Button key={i} onClick={() => {this.handleChange(d.name, d.source)}} style={{height: '80px', color: '#000A40'}}>{d.name}</Button>
            }
          })})
      }
    getCameras()
    }

    render(){

        let player = {
            marginTop: '5px'
        }

        let buttonGridStyle={
            width: '100%',
            textAlign:'left',
            overflow: 'horizontal'
        }

        let buttons = {
            marginLeft: '15px', 
            width: '100%',
            color: '#000A40',
            overflow: 'hidden'
        }

        return(
            <div>
                <div style={{width: '700px', height: '420px', backgroundColor: '#F6F7F8'}}>
                    <ReactPlayer url={this.state.source} playing muted controls={false} className={player} width="100%" height="100%"/>
                    <div style={{marginTop: '15px'}}>
                        <h1 style={{marginLeft: '15px', color: '#000A40'}}>{this.state.header}</h1>
                    </div>
                    <div style={buttonGridStyle}>
                        <div style={buttons}>
                            {this.state.buttonNames.map((d) => {
                              return d
                            })}
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default MainFeed
