import React, {Component} from 'react';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Paper from '@material-ui/core/Paper';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import { createMuiTheme } from '@material-ui/core/styles';
import { withStyles, ThemeProvider } from '@material-ui/core/styles';
import NavigationItem from '../navigationItem/navigationItem'
import Snack from '../snackbar/snackbar'
import { withRouter } from 'react-router-dom'

const logo = require('../../assets/images/logo0.svg')

const Axios = require('axios').default

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright © '}
      <Link color="inherit" href="https://material-ui.com/">
        Secure Vision
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const useStyles = (theme => ({
  root: {
    height: '100vh'
  },
  image: {
    // backgroundImage: 'url(https://images.pexels.com/photos/159823/kids-girl-pencil-drawing-159823.jpeg?cs=srgb&dl=girls-on-desk-looking-at-notebook-159823.jpg&fm=jpg)',
    backgroundRepeat: 'no-repeat',
    backgroundColor:
      theme.palette.type === 'dark' ? theme.palette.grey[900] : theme.palette.grey[50],
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundColor: '#000A40',
    width: '60%'
  },
  paper: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    height: '100vh',
    backgroundColor: '#F6F7F8',
  },
  form: {
    width: '40%', // Fix IE 11 issue.
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
  title:{
      fontSize: '70px',
      marginTop: '-20%',
      color: '#000A40'
  },
  header:{
      lineHeight: '25px',
      alignItems: 'center',
      textAlign: 'center',
      color: '#000A40'
  },
  link: {
    color: '#000A40'
  }
}));

 class SignInSide extends Component {


  state = {
    canSubmit: false,
    email: '',
    password: '',
    loginError: false 
  }

  submitHandler = async () => {

      let submit = await Axios.post('/api/auth/authcheck',{
          email: this.state.email.toLowerCase(),
          password: this.state.password
        })

      console.log(submit)
      console.log(submit.data.message, 'data white boii')

      if(submit.data.message === 'success'){
        this.props.setUser(submit)
        this.props.history.push('/dashboard')
      }
      else{
        this.setState({loginError: true})
        console.log(this.state)
      }
  }

  emailChangeHandler = (event) => {
    let newState = {...this.state}
    let email = event.target.value 
    newState.email = email 
    this.setState(newState)
  }

  passwordChangeHandler = (event) => {
    let newState = {...this.state}
    let password = event.target.value 
    newState.password = password 
    this.setState(newState)
  }

  render(){

  const {classes} = this.props;

  return (
    // <Grid container component="main" className={classes.root} fullWidth>
      <ThemeProvider>
      <CssBaseline/>
      <Grid item xs={0} sm={18} md={20} component={Paper} elevation={6} square fullWidth fullHeight>
        <div className={classes.paper}>
            <div className={classes.header}>
                {/* <h1 className={classes.title}>Secure Vision</h1> */}
                {/* <h2>The next step in keeping your children safe</h2> */}
            </div>
          <form className={classes.form} noValidate>
          <img src={logo} style={{marginBottom: '40px'}}/>
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              id="email"
              label="Email Address"
              name="email"
              autoComplete="email"
              autoFocus
              onChange={this.emailChangeHandler}
            />
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              name="password"
              label="Password"
              type="password"
              id="password"
              autoComplete="current-password"
              onChange={this.passwordChangeHandler}
            />
            <FormControlLabel
              control={<Checkbox value="remember" color="primary" />}
              label="Remember me"
            />
            <NavigationItem link={this.state.canSubmit ? '/dashboard' : '/'}>
              <Button
              type="submit"
              fullWidth
              variant="contained"
              color="primary"
              className={classes.submit}
              onClick = {this.submitHandler}>
              Sign In
              </Button>
            </NavigationItem>
            <Grid container>
                <Grid item xs>
                  <NavigationItem link='/resetPassword' style={classes.link} color='#6C63FF'>
                    Forgot password?
                  </NavigationItem>
                </Grid>
              <Grid item>
                <NavigationItem link='/register' style={classes.link} color='#6C63FF'>
                    {"Register your school now"}
                </NavigationItem>
              </Grid>
            </Grid>
            <Box mt={5}>
              <Copyright />
            </Box>
          </form>
        </div>
      </Grid>
      <Snack
        activate={this.state.loginError}
        open={this.state.loginError}
      />
      </ThemeProvider>
      /* <Grid item xs={true} sm={4} md={7} className={classes.image} />
      {snack} */
    // </Grid>
  )};
}

export default withRouter(withStyles(useStyles)(SignInSide))
