import React, { Component, createRef } from 'react'
import Button from '@material-ui/core/Button'
import ReactPlayer from 'react-player'
import Axios from 'axios'

const classes = {
    returnArrow: {
        fontSize: '40px',
        position: 'absolute',
        marginTop: '30px'
    },
    buttonsArea: {
        width: '80%',
        overflow: 'scroll',
        height: '80px',
        marginTop: '50px'
    },
    canvasArea: {
        width: '80%',
        height: '400px',
        display: 'flex',
        // backgroundColor: '#333',
        textAlign: 'center',
        justifyContent: 'center',
        marginTop: '5px'
    },
    canvas: {
        backgroundColor: 'rgba(0, 0, 0, .05)',
        width: '80%'
    },
    buttonStyling: {
        marginLeft: '20px',
        marginRight: '20px'
    },
    configButtons: {
        width: '20%',
        height: '100%',
        textAlign: 'left',
        marginLeft: '250px'
    }
}

class LprConfig extends Component{

    canvasWidth = 200
    canvasHeight = 200

    state = {
        currentSource: null,
        camerasList: null,
        videoSource: null,
        currentPointsObject: {
            x0: 0,
            y0: 0, 
            width: 0, 
            height: 0
        },
    }

    canvasRef = createRef() 

    componentDidMount(){

        Axios.get('/api/cameras/getCameras')
        .then(data => {
            this.setState({camerasList: data.data, currentSource: data.data[0]})
            console.log(data)
        }).catch(err => {
            console.log(err)
        })

        const canvas = this.canvasRef.current
        const context = canvas.getContext('2d')

        this.setState({currentPointsObject: {
            x0: canvas.width/2 - 50,
            y0: canvas.height/2 - 50, 
            width:  100, 
            height: 100
        }})

    }

    componentDidUpdate(){

        console.log(this.state)

        const canvas = this.canvasRef.current

        canvas.addEventListener('mousedown', (e) => console.log(e))

        const ctx = canvas.getContext('2d')
        ctx.strokeStyle = '#003D96'

        const canvasWidth = canvas.width  
        this.canvasWidth = canvasWidth

        const canvasHeight = canvas.height

        ctx.clearRect(0, 0, canvasWidth, canvasHeight)
        ctx.fill()

        let {x0, y0, width, height} = this.state.currentPointsObject

        ctx.strokeRect(x0, y0, width, height)
        ctx.save()

    }

    render(){

        console.log(this.state.videoSource)

        let buttons = this.state.camerasList ? this.state.camerasList.map((data, index) => {
            return data.isActive ? 
            <Button style={classes.buttonStyling} key={index} onClick={() => {this.setState({currentSource: data.source})}}>
            {data.name}
            </Button> : null
        }) : null 

        return(
            <div>
                <h1>Configure License Plate Detection Zone</h1>
                <p>Select a camera feed to configure its license plate detection area. Only license plates within the desingated box will be detected.</p>
                <div style={classes.buttonsArea}>
                {buttons}
                </div>
                <div style={classes.canvasArea}>
                    <canvas style={classes.canvas} ref={this.canvasRef}></canvas>
                    <video src={require('../../assets/videos/headOn.mp4')} ref="video" width="50%" style={{display: 'none'}}></video>
                    <div style={classes.configButtons}>
                    <Button onClick={
                        () => { 

                            const x0 = this.state.currentPointsObject.x0 
                            const y0 = this.state.currentPointsObject.y0 - 2
                            const width = this.state.currentPointsObject.width
                            const height = this.state.currentPointsObject.height 

                            const coordsObj = {
                                x0: x0, 
                                y0: y0,
                                width: width,
                                height: height
                            }

                            this.setState({currentPointsObject: coordsObj})
                        }
                    }
                    style={{textAlign: 'left', marginBottom: '10px'}}
                    >Vertical Up
                    </Button>
                    <Button onClick={
                        () => { 

                            const x0 = this.state.currentPointsObject.x0  
                            const y0 = this.state.currentPointsObject.y0 + 2
                            const width = this.state.currentPointsObject.width
                            const height = this.state.currentPointsObject.height 

                            const coordsObj = {
                                x0: x0, 
                                y0: y0,
                                width: width,
                                height: height
                            }

                            this.setState({currentPointsObject: coordsObj})
                        }
                    }
                    style={{textAlign: 'left', marginBottom: '10px'}}
                    >Vertical Down
                    </Button>
                    <Button onClick={
                        () => { 

                            const x0 = this.state.currentPointsObject.x0 - 2
                            const y0 = this.state.currentPointsObject.y0 
                            const width = this.state.currentPointsObject.width
                            const height = this.state.currentPointsObject.height 

                            const coordsObj = {
                                x0: x0, 
                                y0: y0,
                                width: width,
                                height: height
                            }

                            this.setState({currentPointsObject: coordsObj})
                        }
                    }
                    style={{textAlign: 'left', marginBottom: '10px'}}
                    >Horizontal Left 
                    </Button>
                    <Button onClick={
                        () => { 

                            const x0 = this.state.currentPointsObject.x0 + 2
                            const y0 = this.state.currentPointsObject.y0 
                            const width = this.state.currentPointsObject.width
                            const height = this.state.currentPointsObject.height 

                            const coordsObj = {
                                x0: x0, 
                                y0: y0,
                                width: width,
                                height: height
                            }

                            this.setState({currentPointsObject: coordsObj})
                        }
                    }
                    style={{textAlign: 'left', marginBottom: '10px'}}
                    >Horizontal Right
                    </Button>
                    <Button onClick={
                        () => { 

                            const x0 = this.state.currentPointsObject.x0  
                            const y0 = this.state.currentPointsObject.y0 
                            const width = this.state.currentPointsObject.width + 2
                            const height = this.state.currentPointsObject.height 

                            const coordsObj = {
                                x0: x0, 
                                y0: y0,
                                width: width,
                                height: height
                            }

                            this.setState({currentPointsObject: coordsObj})
                        }
                    }
                    style={{textAlign: 'left', marginBottom: '10px'}}
                    >Expand Width
                    </Button>
                    <Button onClick={
                        () => { 

                            const x0 = this.state.currentPointsObject.x0  
                            const y0 = this.state.currentPointsObject.y0 
                            const width = this.state.currentPointsObject.width - 2
                            const height = this.state.currentPointsObject.height 

                            const coordsObj = {
                                x0: x0, 
                                y0: y0,
                                width: width,
                                height: height
                            }

                            this.setState({currentPointsObject: coordsObj})
                        }
                    }
                    style={{textAlign: 'left', marginBottom: '10px'}}
                    >Minimize Width
                    </Button>
                    <Button onClick={
                        () => { 

                            const x0 = this.state.currentPointsObject.x0  
                            const y0 = this.state.currentPointsObject.y0 
                            const width = this.state.currentPointsObject.width 
                            const height = this.state.currentPointsObject.height + 2 

                            const coordsObj = {
                                x0: x0, 
                                y0: y0,
                                width: width,
                                height: height
                            }

                            this.setState({currentPointsObject: coordsObj})
                        }
                    }
                    style={{textAlign: 'left', marginBottom: '10px'}}
                    >Expand Height
                    </Button>
                    <Button onClick={
                        () => { 

                            const x0 = this.state.currentPointsObject.x0  
                            const y0 = this.state.currentPointsObject.y0 
                            const width = this.state.currentPointsObject.width 
                            const height = this.state.currentPointsObject.height -2

                            const coordsObj = {
                                x0: x0, 
                                y0: y0,
                                width: width,
                                height: height
                            }

                            this.setState({currentPointsObject: coordsObj})
                        }
                    }
                    style={{textAlign: 'left', marginBottom: '10px'}}
                    >Minimize Height
                    </Button>
                </div>
                    {/* <ReactPlayer url={this.state.currentSource} ref="video"/> */}
                </div>
                <a href="/dashboard/configuration" style={{textDecoration: 'none', color: '#000'}}>
                    <span class="material-icons" style={classes.returnArrow}>
                        arrow_back
                    </span>  
                </a>           
            </div>
        )
    }
}

export default LprConfig
