const Axios = require('axios')

const sendImage = () => {
    // Axios.get('/api/getPlates/sendToAPI')

    let constraints = {
        audio: false,
        video: {width: 640, height: 480}
    }

    navigator.mediaDevices.getUserMedia(constraints)
    .then(success)
    .catch(error)

    function success(stream){
        let video = document.getElementById('video')
        video.setAttribute('controls', 'controls')
        video.src = './security.mp4'

        let canvas = document.createElement('canvas')
        canvas.width = 400
        canvas.height = 400
        let context = canvas.getContext('2d')
        context.globalCompositeOperation = 'difference'

        setInterval(capture, 2000)

        function capture() {

            context.drawImage(video, 0, 0, 640, 480)

            let image = new Image()
            image.src = canvas.toDataURL("image/png")
            console.log(image.src)

            // Axios.post('/api/getPlates/sendToAPI', 
            // {image_url: 'https://ewscripps.brightspotcdn.com/dims4/default/3a5c97e/2147483647/strip/true/crop/640x360+0+48/resize/1280x720!/quality/90/?url=https%3A%2F%2Fmediaassets.theindychannel.com%2Fphoto%2F2018%2F06%2F26%2Flicense%20plates_1530064394089.JPG_90880759_ver1.0_640_480.jpg',
            // base64string: dataURL})
        }

    }

    function error(error){
        console.log(error)
    }
}

module.exports = sendImage
