import React, {Component} from 'react';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Paper from '@material-ui/core/Paper';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import { createMuiTheme } from '@material-ui/core/styles';
import { withStyles, ThemeProvider } from '@material-ui/core/styles';
import NavigationItem from '../navigationItem/navigationItem'
const Axios = require('axios').default

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright Â© '}
      <Link color="inherit" href="https://material-ui.com/">
        Secure Vision
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const useStyles = (theme => ({
  root: {
    height: '100vh',
  },
  image: {
    backgroundImage: 'url(https://images.pexels.com/photos/159823/kids-girl-pencil-drawing-159823.jpeg?cs=srgb&dl=girls-on-desk-looking-at-notebook-159823.jpg&fm=jpg)',
    backgroundRepeat: 'no-repeat',
    backgroundColor:
      theme.palette.type === 'dark' ? theme.palette.grey[900] : theme.palette.grey[50],
    backgroundSize: 'cover',
    backgroundPosition: 'center',
  },
  paper: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    height: '100%',
    width: '100%'
  },
  form: {
    width: '80%', // Fix IE 11 issue.
    marginTop: '5%'
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
  title:{
      fontSize: '70px',
      marginTop: '-20%',
      color: '#ffeb3b'
  },
  header:{
      lineHeight: '25px',
      alignItems: 'center',
      textAlign: 'center',
      color: '#ffeb3b'
  },
  link: {
    color: '#ffeb3b'
  }
}));

 class SignInSide extends Component {

  state = {
    canSubmit: false,
    email: '',
    password: ''
  }

  componentDidMount(){
    // check whether current user is already logged in
    // if user is logged in, go right to dashboard and pass in their data
  }

  submitHandler = async () => {

      let submit = await Axios.post('/api/auth/authcheck',{
          email: this.state.email.toLowerCase(),
          password: this.state.password
        })
      console.log(submit.data.message, 'data white boii')

      this.setState({
        canSubmit:submit.data.message
      })
  }

  emailChangeHandler = (event) => {
    let newState = {...this.state}
    let email = event.target.value 
    newState.email = email 
    this.setState(newState)
  }

  passwordChangeHandler = (event) => {
    let newState = {...this.state}
    let password = event.target.value 
    newState.password = password 
    this.setState(newState)
  }

  render(){

  const {classes} = this.props;

  let darkTheme = createMuiTheme({
    palette: {
      type: 'dark',
      primary: {
        main: '#ffeb3b'
      }
    }
  })



  return (
    <Grid container component="main" className={classes.root}>
      <ThemeProvider theme={darkTheme}>
      <CssBaseline/>
      <Grid item xs={false} sm={4} md={7} className={classes.image} />
      <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
        <div className={classes.paper}>
            <div className={classes.header}>
                <h1 className={classes.title}>Secure Vision</h1>
                <h2>The next step in keeping your children safe</h2>
            </div>
          <form className={classes.form} noValidate>
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              id="email"
              label="Email Address"
              name="email"
              autoComplete="email"
              autoFocus
              onChange={this.emailChangeHandler}
            />
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              name="password"
              label="Password"
              type="password"
              id="password"
              autoComplete="current-password"
              onChange={this.passwordChangeHandler}
            />
            <FormControlLabel
              control={<Checkbox value="remember" color="primary" />}
              label="Remember me"
            />
            <NavigationItem link={this.state.canSubmit ? '/dashboard' : '/login'}>
              <Button
              type="submit"
              fullWidth
              variant="contained"
              color="primary"
              className={classes.submit}
              onClick = {() => this.submitHandler()}>
              Sign In
              </Button>
            </NavigationItem>
            <Grid container>
                <Grid item xs>
                  <NavigationItem link='/resetPassword' style={classes.link}>
                    Forgot password?
                  </NavigationItem>
                </Grid>
              <Grid item>
                <NavigationItem link='/register' style={classes.link}>
                    {"Register your school now"}
                </NavigationItem>
              </Grid>
            </Grid>
            <Box mt={5}>
              <Copyright />
            </Box>
          </form>
        </div>
      </Grid>
      </ThemeProvider>
    </Grid>
  )};
}

export default withStyles(useStyles)(SignInSide)
