import React, {Component} from 'react'
import TableFixedHeader from './completeLog'
import Snackbar from '../snackbar/snackbar'

const Axios = require('axios')

class activityLayout extends Component{

  intervalId

  state = {
    dataList: [],
    triggerHighRisk: false,
    mostRecentCriminalNumber: '',
    mostRecentCriminalType: '',
    mostRecentCriminalTime: '' 
  }

  refresh = async() => {
    let data = await Axios.get('/api/lpr/getGeneralData')
    let rawData = data.data

    let sorted = rawData.sort((a,b) => (a.date < b.date) ? 1: (a.date === b.date) ? ((a.time < b.time) ? 1 : -1): -1)
    let currentList = this.state.dataList

    // let newPlates = sorted.filter(item => !currentList.includes(item))
    // console.log(newPlates)

    if(currentList.length != sorted.length && currentList.length != 0){

        console.log('recognized a plate')

        this.setState({dataList: sorted, highRiskAlert: null})
        return 
    }

    this.setState({dataList: sorted, highRiskAlert: null})
  }

  componentDidMount(){
    this.refresh()
    this.intervalId = setInterval(this.refresh, 6000)
  }

  componentWillMount(){
      clearInterval(this.intervalId)
  }


  render(){
    return(
        <div>
            <TableFixedHeader dataList={this.state.dataList}/>
            <Snackbar 
                      shouldOpen={this.state.triggerHighRisk} 
                      message={[
                          <span style={{fontSize: '24px', marginBottom: '25px', color: ''}}>High Risk Plate Detected</span>,
                          <br></br>,<br></br>,
                          <span style={{fontSize: '20px', marginBottom: '15px'}}>Plate: {this.state.mostRecentCriminalNumber}</span>,
                          <br></br>,
                          <span style={{fontSize: '20px', marginBottom: '15px'}}>Threat Type: {this.state.mostRecentCriminalType}</span>,
                          <br></br>,
                          <span style={{fontSize: '20px', marginBottom: '15px'}}>Location: {this.state.mostRecentCriminalTime}</span>
                      ]
                      }>
                  </Snackbar>
        </div>
       )
    }
}

export default activityLayout
