import React, { Component } from 'react'
import Modal from '../material/modal'
import Button from '@material-ui/core/Button'
import { TextField } from '@material-ui/core'

const Axios = require('axios')

class addCameraModal extends Component{

    componentDidUpdate(){
        console.log("Add modal mounted")
        console.log(this.state.open)
        if(this.props.open){
            console.log("Modal should open")
            this.setState({open: true})
        }
    }

    state = {
        open: false,
        source: '',
        name: '',
        port: ''
    }

    handleFormSubmit = () => {
        let req = Axios.post('/api/cameras/registerCamera', {
            source: this.state.source,
            name: this.state.name,
            port: this.state.port
        })

        this.setState({open: false})
        console.log(req)
    }

    updateSource = (event) => {
        this.setState({source: event.target.value})
    }

    updateName = (event) => {
        this.setState({name: event.target.value})
    }

    updatePort = (event) => {
        this.setState({port: event.target.value})
    }

    triggerClose = () => {
        this.setState({open: false})
    }

    render(){
        return(
            <Modal title="Register a new camera" shouldOpen={this.state.open}>
                <div style={{width: '50%', lineHeight: '65px'}}>
                    <TextField label="IP Address" style={{width: '100%'}} variant="outlined" value={this.state.source} onChange={this.updateSource}></TextField>
                    <TextField label="Name" style={{width: '100%'}} variant="outlined" value={this.state.name} onChange={this.updateName}></TextField>
                    <TextField label="Port" style={{width: '100%'}} variant="outlined" value={this.state.port} onChange={this.updatePort}></TextField>
                </div>
                <Button onClick = {() => {this.handleFormSubmit()}}>Submit</Button>
                <Button onClick = {() => {this.triggerClose()}}>Cancel</Button>
            </Modal>
        )
    }
}

export default addCameraModal
