import React, {useState, Component} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button'
import Modal from '../popups/modal'

const Axios = require('axios')

const useStyles = makeStyles(theme => ({
  root: {
    left: 0,
    zIndex: '100',
    height: 500,
    position: 'relative',
    width: '100%',
    flexGrow: 1,
    minWidth: 300,
    transform: 'translateZ(0)',
    // The position fixed scoping doesn't work in IE 11.
    // Disable this demo to preserve the others.
    '@media all and (-ms-high-contrast: none)': {
      display: 'none',
    },
  },
  modal: {
    display: 'flex',
    padding: theme.spacing(1),
    alignItems: 'center',
    justifyContent: 'center',
  },
  paper: {
    width: 900,
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
  form:{
      padding: '15px',
      display: 'grid',
      lineHeight: '20px'
  },
  label:{
      marginBottom: '10px',
  }
}));

class RegisterModal extends Component{

  rootRef = React.useRef(null);

  state = {
    parentName: '',
    studentName: '',
    plate: '',
    confirmPlate: '',
    showModal: false
  }

  showModal = () => {
    if(this.validateRegistry()){
      console.log("Modal show")
      this.setState({showModal: true})
    }
  }

  submitHandler = async() => {

        if(this.validateRegistry()){
          let submit = await Axios.post('/api/registration/plateRegistration',{
            parentName: this.state.parentName,
            studentName: this.state.studentName,
            plateNumber: this.state.plate,
            isActive: true 
          }).catch((e) => console.log(e))

          console.log(submit)
        }
        else{
          console.log("Invalid register data")
        }
    } 

    parentNameChangeHandler = (event) => {
        this.setState({parentName: event.target.value}) 
    }

    studentNameChangeHandler = (event) => {
      this.setState({studentName: event.target.value})
    }

    plateChangeHandler = (event) => {
      this.setState({plate: event.target.value}) 
    }

    confirmPlateChangeHandler = (event) => {
      this.setState({confirmPlate: event.target.value})
    }

    validateRegistry = () => {
        return((this.state.plate === this.state.confirmPlate) & (this.state.parentName !== "") & (this.state.studentName !== "") & (this.state.plate !== ""))
    }
  render(){

  const classes = useStyles();

  return (
    <div  ref={rootRef}>
      <div >
          <h2 id="server-modal-title">Register a new license plate</h2>
          <form className={classes.form}>
        <label className = {classes.label}>
            <TextField
            id="1"
            label="Parent Name"
            variant="outlined"
            color="secondary"
            onChange = {this.parentNameChangeHandler}
            />
        </label>
        <label className = {classes.label}>
            <TextField
                id="2"
                label="Student Name"
                variant="outlined"
                color="secondary"
                onChange = {this.studentNameChangeHandler}
            />
        </label>
        <label className = {classes.label}>
            <TextField
                id="3"
                label="Plate number"
                variant="outlined"
                color="secondary"
                onChange = {this.plateChangeHandler}
            />
        </label>
        <label className = {classes.label}>
            <TextField
                id="4"
                label="Confirm plate number"
                variant="outlined"
                color="secondary"
                onChange = {this.confirmPlateChangeHandler}
            />
        </label>
        <label className = {classes.label}>
            <Button variant = "outlined" onClick = {this.state.showModal}>
                Add user
            </Button>
        </label>
        </form>
        <Modal 
          shouldOpen={this.state.showModal}
          title="Confirm Registry">
            {/* <p>Parent Name: {parentName}</p>
            <p>Student Name: {studentName}</p>
            <p>Plate Number: {plate}</p>
            <Button variant = "outlined" onClick = {showModal} style={{marginTop: '20px'}}>
                Confirm Add user
            </Button> */}
        </Modal>
        </div>
    </div>
  )};
}

export default RegisterModal
