import React, { Component } from 'react'
import VideoComponent from './VideoComponent'
import Grid from '@material-ui/core/Grid'
import Paper from '@material-ui/core/Paper'
import MainFeed from './mainFeed'

const Axios = require('axios')

class CameraLayout extends Component {

    state = {
        camerasList: [],
        cameraNames: []
    }

    componentDidMount(){
        // get all current cameras in the DB and 
        const getCameras = async() => {
            let result = await Axios.get('/api/cameras/getCameras')
            console.log(result)
            this.setState({camerasList: result.data})
            this.setState({cameraNames: result.data.map((d) => {
              if(d.isActive){
                return d.name
            }
              })})
        }
        getCameras()
    }

    render(){

    let scrollSideStyle = {height:'700px', width: '400px', position: 'absolute', marginLeft: '140px', overflow: 'scroll', textAlign: 'center'}

    return(
    <div style={{width: '100%', height: '700px'}}>
        <Grid container spacing={3}>
        <Grid item xs={6}>
          <Paper style={{width:'700px', height:'6500px' ,marginRight: '400px', backgroundColor: '#E5E5E5'}}>
            <MainFeed name={"Main Entrance"} buttonNames={this.state.cameraNames}/>
          </Paper>
        </Grid>
        <Grid item xs={6}>
          <div style={scrollSideStyle}>
            <div style={{gridTemplateRows: 'repeat(6, 325px)', width: '30%', marginLeft: '50px'}}>
                {this.state.camerasList.map((d, i) => {
                    if(d.isActive){
                      return(<VideoComponent key={i} source={d.source} name={d.name} port={d.port}></VideoComponent>)
                    }
                })}
            </div>
          </div>
        </Grid>
      </Grid>
        {/* <div style={{width: '700px', height: '100%', backgroundColor: '#333'}}>

        </div>
        <div style={{gridTemplateRows: 'repeat(3, 325px)', backgroundColor: '#333', width: '30%', right:'0'}}>
            {this.state.camerasList.map((d, i) => {
                return(<VideoComponent key={i} source={d.source} name={d.name} port={d.port}></VideoComponent>)
            })}
        </div> */}
    </div>
    )}
}

export default CameraLayout
