import React, { Component, createRef } from 'react'
import Button from '@material-ui/core/Button'
import ReactPlayer from 'react-player'
import Axios from 'axios'

const classes = {
    returnArrow: {
        fontSize: '40px',
        position: 'absolute',
        marginTop: '30px'
    },
    buttonsArea: {
        width: '100%',
        overflow: 'scroll',
        height: '80px',
        marginTop: '30px'
    },
    canvasArea: {
        width: '80%',
        height: '400px',
        display: 'flex',
        // backgroundColor: '#333',
        textAlign: 'center',
        justifyContent: 'center',
        marginTop: '5px'
    },
    canvas: {
        backgroundColor: 'rgba(0, 0, 0, .1)',
        position: 'absolute',
        zIndex: '20',
        width: '640px',
        height: '360px'
    },
    buttonStyling: {
        marginLeft: '20px',
        marginRight: '20px'
    },
    configButtons: {
        width: '20%',
        height: '100%',
        textAlign: 'left',
        marginLeft: '250px',
        marginLeft: '80%'
    }
}

class LprConfig extends Component{

    canvasWidth = 200
    canvasHeight = 200

    state = {
        currentSource: null,
        camerasList: null,
        videoSource: null,
        currentPointsObject: {
            x0: 0,
            y0: 0, 
            width: 0, 
            height: 0
        },
        playing: false,
        currentCameraID: null  
    }

    canvasRef = createRef() 
    videoRef = createRef()

    componentDidMount(){

        let source 

        Axios.get('/api/cameras/getCameras')
        .then(data => {

            source = data.data[0].source

            this.setState({camerasList: data.data, currentSource: data.data[0].source, playing: true})
            console.log(data)
        }).catch(err => {
            console.log(err)
        })

        const canvas = this.canvasRef.current

        this.setState({currentPointsObject: {
            x0: canvas.width/2 - 50,
            y0: canvas.height/2 - 50, 
            width:  100, 
            height: 100
        }})
    }

    componentDidUpdate(){

        console.log(this.state)

        const canvas = this.canvasRef.current

        canvas.addEventListener('mousedown', (e) => console.log(e))

        const ctx = canvas.getContext('2d')
        ctx.strokeStyle = '#000'

        const canvasWidth = canvas.width  
        this.canvasWidth = canvasWidth

        const canvasHeight = canvas.height

        ctx.clearRect(0, 0, canvasWidth, canvasHeight)
        ctx.fill()

        let {x0, y0, width, height} = this.state.currentPointsObject
        let vid = document.getElementById('vid')

        // ctx.drawImage(vid, 0, 0, canvasWidth, canvasHeight)
        ctx.strokeRect(x0, y0, width, height)
        ctx.save()
    }

    render(){

        console.log(this.state.currentSource, this.state.currentCameraID)

        let buttons = this.state.camerasList ? this.state.camerasList.map((data, index) => {
            return data.isActive ? 
            <Button style={classes.buttonStyling} key={index} onClick={() => {this.setState({
                currentSource: data.source,
                currentCameraID: data._id
            })}}>
            {data.name}
            </Button> : null
        }) : null 

        return(
            <div>
                <h1>Configure License Plate Detection Zone</h1>
                <p>Select a camera feed to configure its license plate detection area. Only license plates within the desingated box will be detected.</p>
                <div style={classes.buttonsArea}>
                {buttons}
                </div>
                <div style={classes.canvasArea}>
                    <div style={{backgroundColor: 'red', overflow: 'hidden'}}>
                        <canvas style={classes.canvas} ref={this.canvasRef}></canvas>
                        <video 
                            src={require('../../assets/videos/headOn.mp4')} 
                            ref={this.videoRef} id="vid" 
                            playing={this.state.playing}
                            style={{
                                position: 'absolute'
                            }}
                            >    
                        </video>
                    </div>

                    <div style={classes.configButtons}>
                    <Button onClick={
                        () => { 

                            const x0 = this.state.currentPointsObject.x0 
                            const y0 = this.state.currentPointsObject.y0 - 3
                            const width = this.state.currentPointsObject.width
                            const height = this.state.currentPointsObject.height 

                            const coordsObj = {
                                x0: x0, 
                                y0: y0,
                                width: width,
                                height: height
                            }

                            this.setState({currentPointsObject: coordsObj})
                        }
                    }
                    style={{textAlign: 'left', marginBottom: '10px'}}
                    >Vertical Up
                    </Button>
                    <Button onClick={
                        () => { 

                            const x0 = this.state.currentPointsObject.x0  
                            const y0 = this.state.currentPointsObject.y0 + 3
                            const width = this.state.currentPointsObject.width
                            const height = this.state.currentPointsObject.height 

                            const coordsObj = {
                                x0: x0, 
                                y0: y0,
                                width: width,
                                height: height
                            }

                            this.setState({currentPointsObject: coordsObj})
                        }
                    }
                    style={{textAlign: 'left', marginBottom: '10px'}}
                    >Vertical Down
                    </Button>
                    <Button onClick={
                        () => { 

                            const x0 = this.state.currentPointsObject.x0 - 3
                            const y0 = this.state.currentPointsObject.y0 
                            const width = this.state.currentPointsObject.width
                            const height = this.state.currentPointsObject.height 

                            const coordsObj = {
                                x0: x0, 
                                y0: y0,
                                width: width,
                                height: height
                            }

                            this.setState({currentPointsObject: coordsObj})
                        }
                    }
                    style={{textAlign: 'left', marginBottom: '10px'}}
                    >Horizontal Left 
                    </Button>
                    <Button onClick={
                        () => { 

                            const x0 = this.state.currentPointsObject.x0 + 3
                            const y0 = this.state.currentPointsObject.y0 
                            const width = this.state.currentPointsObject.width
                            const height = this.state.currentPointsObject.height 

                            const coordsObj = {
                                x0: x0, 
                                y0: y0,
                                width: width,
                                height: height
                            }

                            this.setState({currentPointsObject: coordsObj})
                        }
                    }
                    style={{textAlign: 'left', marginBottom: '10px'}}
                    >Horizontal Right
                    </Button>
                    <Button onClick={
                        () => { 

                            const x0 = this.state.currentPointsObject.x0  
                            const y0 = this.state.currentPointsObject.y0 
                            const width = this.state.currentPointsObject.width + 3
                            const height = this.state.currentPointsObject.height 

                            const coordsObj = {
                                x0: x0, 
                                y0: y0,
                                width: width,
                                height: height
                            }

                            this.setState({currentPointsObject: coordsObj})
                        }
                    }
                    style={{textAlign: 'left', marginBottom: '10px'}}
                    >Expand Width
                    </Button>
                    <Button onClick={
                        () => { 

                            const x0 = this.state.currentPointsObject.x0  
                            const y0 = this.state.currentPointsObject.y0 
                            const width = this.state.currentPointsObject.width - 3
                            const height = this.state.currentPointsObject.height 

                            const coordsObj = {
                                x0: x0, 
                                y0: y0,
                                width: width,
                                height: height
                            }

                            this.setState({currentPointsObject: coordsObj})
                        }
                    }
                    style={{textAlign: 'left', marginBottom: '10px'}}
                    >Minimize Width
                    </Button>
                    <Button onClick={
                        () => { 

                            const x0 = this.state.currentPointsObject.x0  
                            const y0 = this.state.currentPointsObject.y0 
                            const width = this.state.currentPointsObject.width 
                            const height = this.state.currentPointsObject.height + 3

                            const coordsObj = {
                                x0: x0, 
                                y0: y0,
                                width: width,
                                height: height
                            }

                            this.setState({currentPointsObject: coordsObj})
                        }
                    }
                    style={{textAlign: 'left', marginBottom: '10px'}}
                    >Expand Height
                    </Button>
                    <Button onClick={
                        () => { 

                            const x0 = this.state.currentPointsObject.x0  
                            const y0 = this.state.currentPointsObject.y0 
                            const width = this.state.currentPointsObject.width 
                            const height = this.state.currentPointsObject.height - 3

                            const coordsObj = {
                                x0: x0, 
                                y0: y0,
                                width: width,
                                height: height
                            }

                            this.setState({currentPointsObject: coordsObj})
                        }
                    }
                    style={{textAlign: 'left', marginBottom: '10px'}}
                    >Minimize Height
                    </Button>
                </div>
                    {/* <ReactPlayer url={this.state.currentSource} ref="video"/> */}
                </div>
                <a href="/dashboard/configuration" style={{textDecoration: 'none', color: '#000', }}>
                        <span class="material-icons" style={classes.returnArrow}>
                            arrow_back
                        </span>  
                    </a>
                <div style={{display: 'flex', marginLeft: '8%', alignItems: 'center', marginTop: '3%'}}>
                    <Button>
                        Save Configuration 
                    </Button> 
                    <Button>
                        Revert Changes
                    </Button>
                </div>          
            </div>
        )
    }
}

export default LprConfig
