import React, { Component } from 'react'
import { Button } from '@material-ui/core'
import { MdPhoneForwarded } from 'react-icons/md'
import Modal from '../../popups/modal'

let style = {
    alignItems: 'center',
    textAlign: 'center',
    justifyContent: 'center',
    width: '100%'
}

class emergencySection extends Component{

    state = {
        openContacts: false
    }

    toggleModal = () => {
        currentOpen = this.state.openContacts
        this.setState({openContacts: !currentOpen})
    }

    render(){
        return(
        <div style={style}>
            <Button onClick={()=>{this.toggleModal()}} style={{width: '100%', height: '100%', fontSize: '120px', marginTop: '-5px', color: '#110052'}}>
                <MdPhoneForwarded></MdPhoneForwarded>
            </Button>
            <p style={{fontSize: '20px', marginTop: '5px'}}><strong>Emergency Contacts</strong></p>
            <Modal shouldOpen={this.state.openContacts} title></Modal>
        </div>
        )}
}

export default emergencySection
