import React, { Component } from 'react'
import Modal from '../popups/modal'
import Snack from '../snackbar/snackbar'
import Button from '@material-ui/core/Button'
import { TextField } from '@material-ui/core'

const Axios = require('axios')

let editDltStyle = {
    float: "right", 
    marginRight: "30px",
    'p:hover': 'cursor: crosshair'
}

class feedItem extends Component {

    state = {
        showEditModal: false, 
        showDeactivateModal: false,
        parentName: this.props.parentName,
        studentName: this.props.studentName,
        plateNumber: this.props.plateNumber,
        confirmPlate: this.props.plateNumber,
        id: this.props.id,
        showSuccess: false,
        display: true
    }

    componentDidMount(){
        console.log(this.props)
    }

    openEditModal = () => {
        console.log('open pls')
          this.setState({showEditModal: true})
    }

    openDeactivateModal = () => {
        console.log('open pls')
          this.setState({showDeactivateModal: true})
    }

    handleEditModalClose = () => {
        this.setState({showEditModal: false})

    }

    handleDeactivateModalClose = () => {
        this.setState({showDeactivateModal: false})
    }

    editItemHandler = async() => {
        if(this.state.plateNumber === this.state.confirmPlate){
        let editItem = await Axios.put('/api/edit/editPlate', {
                id: this.state.id, 
                parentName: this.state.parentName,
                studentName: this.state.studentName,
                plateNumber: this.state.plateNumber
            })
        console.log(editItem)
        this.setState({
            showEditModal: false
        })
        this.setState({showSuccess: true})}
        else{
            console.log("Plates do not match")
        }
    }

    deactivatetemHandler = async() => {
        let deactivate = await Axios.put('/api/edit/deactivate', {
                id: this.state.id 
            })
        console.log(deactivate)
        this.setState({showDeactivateModal: false})
        this.setState({showSuccess: true})
        this.setState({display:false})
    }

    parentNameChangeHandler = (event) => {
        this.setState({parentName: event.target.value}) 
    }

    studentNameChangeHandler = (event) => {
      this.setState({studentName: event.target.value})
    }

    plateChangeHandler = (event) => {
      this.setState({plateNumber: event.target.value}) 
    }

    confirmPlateChangeHandler = (event) => {
      this.setState({confirmPlate: event.target.value})
    }   

    render(){

    let feedItemStyle
    let successSnack = this.state.showSuccess ? <Snack activate={true} message="Plate successfully edited"></Snack> : null

    feedItemStyle = this.state.display ? {
        width: '100%',
        height: '70px',
        border: '1px solid #fff',
        backgroundColor: 'rgba(0, 0, 0, .3)',
        borderRadius: '5px',
        boxShadow: '1px 1px #333',
        color: '#fff',
        fontSize: '20px',
        marginTop: '10px'
    } : {display: 'none'}

    return(
        <div>
        <div style = {feedItemStyle}>
            <p style={{float: "left", marginLeft: '30px'}}>{this.state.plateNumber}</p>
            <p style={{float: "left", marginLeft: '60px'}}>{this.state.parentName}</p>
            <p style={editDltStyle}><i className={"fas fa-minus-circle"} onClick={() => this.openDeactivateModal()}></i></p>
            <p style={editDltStyle}><i className={"fas fa-pen"} onClick={() => this.openEditModal()}></i></p>
            <Modal 
            shouldOpen={this.state.showEditModal}
            title="Edit Plate">
                <div>Parent Name: <TextField style={{marginLeft: '20px'}} onChange = {this.parentNameChangeHandler} value={this.state.parentName}></TextField></div>
                <div>Student Name: <TextField style={{marginLeft: '20px'}} onChange = {this.studentNameChangeHandler} value={this.state.studentName}></TextField></div>
                <div>Plate Number: <TextField style={{marginLeft: '20px'}} onChange = {this.plateChangeHandler} value={this.state.plateNumber}></TextField></div>
                <div>Confirm Plate Number: <TextField style={{marginLeft: '20px'}} onChange = {this.confirmPlateChangeHandler} value={this.state.confirmPlate}></TextField></div>
                <div>
                    <Button variant = "outlined" onClick = {this.editItemHandler} style={{marginTop: '20px'}}>
                        Confirm Edit
                    </Button>
                    <Button variant = "outlined" onClick = {this.handleEditModalClose} style={{marginTop: '20px', marginLeft: '20px'}}>
                        Cancel
                    </Button>
                </div>
            </Modal>
            <Modal 
            shouldOpen={this.state.showDeactivateModal}
            title="Confirm Deactivation">
                <p>Are you sure you wish to deactivate this plate? Deactivation means cameras will no longer recognize this plate, and it will be removed from the 'active plates' section. Please confirm this action.</p>
                <div>
                    <Button variant = "outlined" onClick = {this.deactivatetemHandler} style={{marginTop: '20px'}}>
                        Confirm Deactivation
                    </Button>
                    <Button variant = "outlined" onClick = {this.handleDeactivateModalClose} style={{marginTop: '20px', marginLeft: '20px'}}>
                        Cancel
                    </Button>
                </div>
            </Modal>
        </div>
        {successSnack}
        </div>
    )}
}

export default feedItem
