import React, { useEffect } from 'react'

let style = {
    marginLeft: '25px',
    lineHeight: '300%'
}

const schoolHeader = (props) => {

    [currentDate, setDate] = React.useState(null)

    const getCurrentDate = () => {

        const today = new Date()

        const dd = String(today.getDate()).padStart(2, '0');
        const mm = String(today.getMonth() + 1).padStart(2, '1')
        const yyyy = today.getFullYear();

        today = mm + '/' + dd + '/' + yyyy
        setDate(today)

    }

    useEffect(() => {
        getCurrentDate()
    }, [])

    return(
        <div style={style}>
            <h1 style={{fontSize: '50px'}}>{props.school}</h1>
            <div style={{display: 'flex'}}>
                <h3 style={{fontSize: '25px'}}>{props.location}</h3>
                <h3 style={{fontSize: '25px', marginLeft: '10%'}}>Current Time: {currentDate}</h3>
            </div>
        </div>
    )
}

export default schoolHeader
