import React, { Component } from 'react';
import LoginScreen from './components/login/login'
import Dashboard from './components/dashboard/dashboard'
// import LprConfig from './components/configuration/lprConfig'
import {Route, BrowserRouter, Redirect} from 'react-router-dom'

class App extends Component {

  state = {
    userObj: null 
  }

  setUser = (data) => {
    this.setState({userObj: data})
  }

  render () {
    return (
      <div>
        <BrowserRouter>
            <Route path="/" exact component={() => <LoginScreen setUser={this.setUser}/>}/>
            <Route path="/dashboard" component={() => <Dashboard user={this.state.userObj}/>}/>
        </BrowserRouter>
      </div>
    );
  }
}

export default App;
