import React, { Component } from 'react'
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button'

import Modal from './registerModal'

const useStyles = makeStyles(theme => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
    },
  },
}));

class userRegistry extends Component {

    state = {
        showModal: false
    }

    registerClickHandler = () => {
        let stateCopy = {...this.state}
        stateCopy.showModal = () => {
            return (stateCopy.showModal ? false : true)
        }
        this.setState(stateCopy)
    }

    render(){
        const classes = useStyles

        let modal = this.state.showModal ? false : <Modal
            shouldOpen = {this.state.showModal}
        />

        return (
            <div className={classes.root}>
                <Button variant="outlined" onClick = {() => {this.registerClickHandler()}}>
                Register
                </Button>
                {modal} 
            </div>
       )
    }
}

export default userRegistry
