import React, { Component } from 'react'

let style = {
    marginLeft: '25px',
    lineHeight: '300%'
}

const getCurrentDate = () => {

    let today = new Date()

    const dd = String(today.getDate()).padStart(2, '0');
    const mm = String(today.getMonth() + 1).padStart(2, '1')
    const yyyy = today.getFullYear();

    today = mm + '/' + dd + '/' + yyyy
    return today 

}

export default class schoolHeader extends Component{

    state = {
        currentDate: 'today' 
    }

    componentWillMount(){
        let today = getCurrentDate()
        this.setState({currentDate: today})
    }

    render(){
        return(
            <div style={style}>
                <h1 style={{fontSize: '50px'}}>{this.props.school}</h1>
                <div style={{display: 'flex'}}>
                    <h3 style={{fontSize: '25px'}}>{this.props.location}</h3>
                    <h3 style={{fontSize: '25px', marginLeft: '10%'}}>Current Time: {this.state.currentDate}</h3>
                </div>
            </div>
        )
    }
}
